'{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",

  "data": {"name": "dataset"}, // data source reference (expects named dataset)

  // TRANSFORMS (derive custom styling and positional helper fields)
  "transform": [
    {
      "name": "colourTest", // assigns a name for reference (optional in Vega-Lite)
      // conditional color assignment: blue if Sign = 1, red otherwise
      "calculate": "datum[''Sign''] == 1 ? ''#0D6ABF'' : ''#A1343C''  ",
      "as": "cf_colour" // stores computed value in a new field ''cf_colour''
    },
    {
      "name": "actualTest",
      // calculates text offset based on Sign and True value thresholds
      "calculate": "datum[''Sign''] == 1 && datum[''True''] > 0.1 ? -44 : datum[''Sign''] == 1 && datum[''True''] <= 0.1 ? -34  : 12  ",
      "as": "cf_text" // output field controlling text position or offset
    },
    {
      "name": "estimateTest",
      // creates placeholder numeric field for future logic (currently always 0)
      "calculate": "datum[''Sign''] == 1 ? 0 : 0  ",
      "as": "cf_estimate"
    }
  ],

  // GLOBAL ENCODING (applies to all marks unless overridden)
  "encoding": {
    "x": {
      "field": "True", // quantitative measure shown on x-axis
      "type": "quantitative", // continuous numeric encoding
      "axis": {
        "title": null, // removes x-axis title
        "labels": false, // hides tick labels
        "grid": false, // removes gridlines
        "domain": true, // keeps baseline visible
        "domainOpacity": 0.2 // faint baseline (subtle visual anchor)
      }
    },
    "y": {
      "field": "Topic", // categorical variable for vertical position
      "type": "nominal",
      "axis": {
        "labelFontSize": 16, // larger text for category labels
        "labelLimit": 0, // prevents label truncation
        "title": null, // hides axis title
        "labelPadding": 50, // spacing between axis labels and marks
        "bandPosition": -0.03, // small upward offset for better alignment
        "domain": false, // hides y-axis baseline
        "gridColor": {
          // conditional gridline coloring (highlight first row differently)
          "condition": {
            "test": {
              "field": "index", // checks the data index
              "equal": 0
            },
            "value": "transparent" // makes first gridline invisible
          },
          "value": "whitesmoke" // light color for other horizontal gridlines
        }
      },
      "title": "Actual", // label for y-axis (semantic rather than visible)
      "sort": {
        "field": "Estimated", // order topics by ''Estimated'' values
        "order": "ascending" // ensures low-to-high order
      }
    }
  },

  "description": "layer1", // optional metadata or description for chart

  // MULTI-LAYERED VISUALIZATION (three marks rendered sequentially)
  "layer": [
    {
      "description": "Mark 1", // connecting line layer
      "mark": {
        "type": "line", // line connecting two x positions
        "opacity": 0.5, // partially transparent for subtle appearance
        "tooltip": true, // enables hover tooltips
        "color": "black" // static line color
      },
      "encoding": {
        "x": {"field": "True"}, // starting x position
        "x2": {"field": "Estimated"} // ending x position (draws a connecting segment)
      }
    },
    {
      "description": "Mark 2", // left-side point marker (True value)
      "mark": {
        "type": "point", // circular point shape
        "size": 150, // pixel size of marker
        "filled": true, // solid fill (not hollow)
        "opacity": 1, // fully opaque
        "color": "gainsboro", // light gray fill for base points
        "stroke": "black", // thin outline for contrast
        "strokeWidth": 0.5 // line thickness for stroke
      },
      "encoding": {
        "x": {"field": "True"} // x-position for actual (True) value
      }
    },
    {
      "description": "Mark 3", // right-side point marker (Estimated value)
      "mark": {
        "type": "point",
        "size": 150,
        "filled": true,
        "opacity": 1,
        "stroke": "black", // thin outline to define point edges
        "strokeWidth": 0.5
      },
      "encoding": {
        "x": {"field": "Estimated"} // x-position for estimated value
        // color for this point comes from transform field ''cf_colour'' (not declared here)
      }
    }
  ]
}'
