Literal.Value:
'{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",

  "data": {"name": "dataset"}, // data source reference (bound externally, e.g., Power BI)

  // TRANSFORMS (compute derived fields for color and label offsets)
  "transform": [
    {
      "name": "colourTest",
      // assigns color by Sign (blue for positive, red for negative)
      "calculate": "datum[''Sign''] == 1 ? ''#0D6ABF'' : ''#A1343C''  ",
      "as": "cf_colour" // field used for consistent color across marks
    },
    {
      "name": "actualTest",
      // text offset logic: varies by Sign and True value size
      "calculate": "datum[''Sign''] == 1 && datum[''True''] > 0.1 ? -44 : datum[''Sign''] == 1 && datum[''True''] <= 0.1 ? -34  : 12  ",
      "as": "cf_text" // defines xOffset for True-side labels
    },
    {
      "name": "estimateTest",
      // placeholder field (currently always 0)
      "calculate": "datum[''Sign''] == 1 ? 0 : 0  ",
      "as": "cf_estimate"
    }
  ],

  // GLOBAL ENCODING (applies to all layers)
  "encoding": {
    "x": {
      "field": "True", // x-axis base field for numeric values
      "type": "quantitative",
      "axis": {
        "title": null, // hides axis title
        "labels": false, // hides x-axis tick labels
        "grid": false, // removes gridlines
        "domain": true, // shows axis baseline
        "domainOpacity": 0.2 // faint axis line for clean layout
      }
    },
    "y": {
      "field": "Topic", // vertical grouping variable
      "type": "nominal", // categorical field
      "axis": {
        "labelFontSize": 16, // large readable text
        "labelLimit": 0, // no truncation of long labels
        "title": null,
        "labelPadding": 50, // distance between axis and marks
        "bandPosition": -0.03, // small offset for better alignment
        "domain": false, // hide y-axis baseline
        "gridColor": {
          // alternate grid color logic (hide first line)
          "condition": {
            "test": {
              "field": "index",
              "equal": 0
            },
            "value": "transparent"
          },
          "value": "whitesmoke"
        }
      },
      "title": "Actual", // semantic label
      "sort": {
        "field": "Estimated", // sort topics by Estimated value
        "order": "ascending"
      }
    }
  },

  "description": "layer1", // spec-level description (optional metadata)

  // LAYERED MARKS
  "layer": [
    {
      "description": "Mark 1",
      "mark": {
        "type": "line", // connector between True and Estimated
        "opacity": 0.5, // semi-transparent
        "tooltip": true, // enables tooltips
        // color dynamically set by calculated field (cf_colour)
        "color": {
          "expr": "datum.cf_colour"
        }
      },
      "encoding": {
        "x": {"field": "True"}, // line start (actual)
        "x2": {"field": "Estimated"} // line end (estimated)
      }
    },
    {
      "description": "Mark 2",
      "mark": {
        "type": "point", // circular point for True value
        "size": 150,
        "filled": true,
        "opacity": 1,
        "color": "gainsboro", // neutral background color
        "stroke": "black", // outline for visibility
        "strokeWidth": 0.5
      },
      "encoding": {
        "x": {"field": "True"} // placed at True position
      }
    },
    {
      "description": "Mark 3",
      "mark": {
        "type": "point", // circular point for Estimated value
        "size": 150,
        "filled": true,
        "opacity": 1,
        "stroke": "black",
        "strokeWidth": 0.5
      },
      "encoding": {
        "x": {"field": "Estimated"},
        // color determined by Sign (-1 or +1)
        "color": {
          "field": "Sign",
          "scale": {
            "domain": [-1, 1], // ensures red = -1, blue = +1
            "range": ["#A1343C", "#0D6ABF"] // explicit color mapping
          },
          "legend": null // removes legend (color used contextually)
        }
      }
    },
    {
      "description": "Mark 4",
      "mark": {
        "type": "text", // label for True value
        "fontSize": 16,
        "baseline": "middle", // vertically center text with mark
        "fontWeight": "bold",
        "color": "#404040", // dark gray text for readability
        "align": "left", // fixed alignment to the right of True mark
        // offset determined by precomputed cf_text field
        "xOffset": {
          "expr": "datum.cf_text"
        },
        "yOffset": 1 // small vertical nudge for balance
      },
      "encoding": {
        // value label from ''True'' field, formatted as percentage
        "text": {
          "field": "True",
          "type": "quantitative",
          "format": "0%", // shows as percentage
          "formatType": "pbiFormat" // Power BI-specific formatter
        }
      }
    },
    {
      "description": "Mark 5",
      "mark": {
        "type": "text", // label for Estimated value
        "fontSize": 16,
        "fontWeight": "bold",
        // dynamic color from calculated field (cf_colour)
        "color": {
          "expr": "datum.cf_colour"
        },
        // conditional alignment depending on Sign
        "align": {
          "expr": "datum[''Sign''] == 1 ? ''left'' : ''right''"
        },
        // small horizontal offset: right for positive, left for negative
        "xOffset": {
          "expr": "datum[''Sign''] == 1 ? 10 : -10"
        },
        "yOffset": 1
      },
      "encoding": {
        "x": {"field": "Estimated"}, // positioned at Estimated value
        "text": {
          "field": "Estimated",
          "type": "quantitative",
          "format": "0%", // show as percentage
          "formatType": "pbiFormat" // Power BI format support
        }
      }
    }
  ]
}'
