Literal.Value:
'{
  "data": {"name": "dataset"}, 
  "transform": [
    {
      "name": "colourTest",
      "calculate": "datum[''Sign''] == 1 ? ''#0D6ABF'' : ''#A1343C''  ",
      "as": "cf_colour"
    },
    {
      "name": "actualTest",
      "calculate": "datum[''Sign''] == 1 && datum[''True''] > 0.1 ? -44 : datum[''Sign''] == 1 && datum[''True''] <= 0.1 ? -34  : 12  ",
      "as": "cf_text"
    },
    {
      "name": "estimateTest",
      "calculate": "datum[''Sign''] == 1 ? 0 : 0  ",
      "as": "cf_estimate"
    }
  ],
  "encoding": {
    "x": {
      "field": "True",
      "type": "quantitative",
      "axis": {
        "title": null,
        "labels": false,
        "grid": false,
        "domain": true,
        "domainOpacity": 0.2
      }
    },
    "y": {
      "field": "Topic",
      "type": "nominal",
      "axis": {
        "labelFontSize": 16,
        "labelLimit": 0,
        "title": null,
        "labelPadding": 50,
        "bandPosition": -0.03,
        "domain": false,
        "gridColor": {
          "condition": {
            "test": {
              "field": "index",
              "equal": 0
            },
            "value": "transparent"
          },
          "value": "whitesmoke"
        }
      },
      "title": "Actual",
      "sort": {
        "field": "Estimated",
        "order": "ascending"
      }
    }
  },
  "description": "layer1",
  "layer": [
    {
      "description": "Mark 1",
      "mark": {
        "type": "line",
        "opacity": 0.5,
        "tooltip": true,
        "color": {
          "expr": "datum.cf_colour"
        }
      },
      "encoding": {
        "x": {"field": "True"},
        "x2": {"field": "Estimated"}
      }
    },
    {
      "description": "Mark 2",
      "mark": {
        "type": "point",
        "size": 150,
        "filled": true,
        "opacity": 1,
        "color": "gainsboro",
        "stroke": "black",
        "strokeWidth": 0.5
      },
      "encoding": {
        "x": {"field": "True"}
      }
    },
    {
      "description": "Mark 3",
      "mark": {
        "type": "point",
        "size": 150,
        "filled": true,
        "opacity": 1,
        "stroke": "black",
        "strokeWidth": 0.5
      },
      "encoding": {
        "x": {"field": "Estimated"},
        "color": {
          "field": "Sign",
          "scale": {
            "domain": [-1, 1],
            "range": [
              "#A1343C",
              "#0D6ABF"
            ]
          },
          "legend": null
        }
      }
    },
    {
      "description": "Mark 4",
      "mark": {
        "type": "text",
        "fontSize": 16,
        "baseline": "middle",
        "fontWeight": "bold",
        "color": "#404040",
        "align": "left",
        "xOffset": {
          "expr": "datum.cf_text"
        },
        "yOffset": 1
      },
      "encoding": {
        "text": {
          "field": "True",
          "type": "quantitative",
          "format": "0%",
          "formatType": "pbiFormat"
        }
      }
    },
    {
      "description": "Mark 5",
      "mark": {
        "type": "text",
        "fontSize": 16,
        "fontWeight": "bold",
        "color": {
          "expr": "datum.cf_colour"
        },
        "align": {
          "expr": "datum[''Sign''] == 1 ? ''left'' : ''right''"
        },
        "xOffset": {
          "expr": "datum[''Sign''] == 1 ? 10 : -10"
        },
        "yOffset": 1
      },
      "encoding": {
        "x": {"field": "Estimated"},
        "text": {
          "field": "Estimated",
          "type": "quantitative",
          "format": "0%",
          "formatType": "pbiFormat"
        }
      }
    }
  ]
