Literal.Value:
'{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "description": "Layered horizontal bar chart showing mean temperature by city with interactive highlight state.",

  "data": {"name": "dataset"},

  // TRANSFORM SECTION
  "transform": [
    {
      // filter to specific cities of interest
      "filter": {
        "field": "City",
        "oneOf": [
          "Swansea", "Newcastle Upon Tyne", "London", "Birmingham",
          "Leeds", "Edinburgh", "Belfast", "Dublin", "Glasgow",
          "Liverpool", "Plymouth", "York", "Cardiff"
        ]
      }
    }
  ],

  // LAYERED STRUCTURE
  "layer": [
    {
      // LAYER 1: background bars (context layer)
      "mark": {
        "type": "bar", // horizontal bar
        "opacity": 0.3, // semi-transparent (background reference)
        "tooltip": true // show tooltip on hover
      },
      "encoding": {
        "x": {
          "field": "Mean Temperature" // base metric for bar length
        }
      }
    },
    {
      // LAYER 2: foreground bars (highlight or selected state)
      "mark": {
        "type": "bar", // same bar geometry
        "tooltip": true
      },
      "encoding": {
        "x": {
          "field": "Mean Temperature__highlight" // field used for selected value
        },
        "opacity": {
          // OPACITY CONTROL (conditional highlight)
          "condition": {
            // hide this layer when selection is "off"
            "test": {"field": "__selected__", "equal": "off"},
            "value": 0 // fully transparent
          },
          "value": 1 // visible by default
        },
        // TOOLTIP (supplementary encoding)
        "tooltip": [
          {
            "field": "Mean Temperature__highlight",
            "title": "Mean Temperature" // tooltip label override
          }
        ]
      }
    }
  ],

  // GLOBAL ENCODING (shared x/y axes)
  "encoding": {
    "y": {
      "field": "City", // city names along vertical axis
      "type": "nominal", // discrete categories
      "axis": {
        "labelFont": "Segoe UI", // custom font for Power BI visual style
        "labelFontSize": 16 // readable city labels
      }
    },
    "x": {
      "type": "quantitative", // numeric axis (temperature scale)
      "axis": {
        "title": "Mean Temperature" // x-axis title
      }
    }
  }
}'
