Literal.Value:
'{
  "data": {"name": "dataset"},
  "transform": [
    {
      "window": [
        {
          "field": "Mean Temperature",
          "op": "mean",
          "as": "10-year Mean"
        }
      ],
      "frame": [-9, 0]
    },
    {
      "fold": [
        "Mean Temperature",
        "10-year Mean"
      ],
      "as": ["Measure", "Value"]
    }
  ],
  "encoding": {
    "x": {
      "field": "CalendarYear",
      "type": "quantitative",
      "axis": {
        "labels": false,
        "format": "#",
        "formatType": "pbiFormat"
      }
    },
    "y": {
      "field": "Mean Temperature",
      "type": "quantitative",
      "axis": {
        "labelFont": "DIN",
        "labelFontSize": 16,
        "tickCount": 3,
        "gridOpacity": 0.3
      },
      "scale": {"zero": false}
    },
    "color": {
      "field": "Measure",
      "type": "nominal",
      "scale": {
        "domain": [
          "Mean Temperature",
          "10-year Mean"
        ],
        "range": ["#B3B3B3", "#404040"]
      },
      "legend": {
        "disable": true,
        "title": "Measure",
        "orient": "top-right",
        "labelFont": "DIN",
        "labelFontSize": 12,
        "titleFont": "DIN",
        "titleFontSize": 14
      }
    },
    "tooltip": [
      {"field": "CalendarYear"},
      {
        "field": "Mean Temperature",
        "type": "quantitative",
        "format": ",.1~f"
      },
      {
        "field": "10-year Mean",
        "type": "quantitative",
        "format": ",.1~f"
      }
    ]
  },
  "layer": [
    {
      "encoding": {
        "y": {
          "field": "Value",
          "type": "quantitative",
          "scale": {"zero": false}
        }
      },
      "height": 200,
      "layer": [
        {

          "mark": {
            "type": "line",
            "strokeWidth": 1,
            "interpolate": "monotone"
          }
        },
        {
          "params": [
            {
              "name": "hover",
              "select": {
                "type": "point",
                "fields": [
                  "CalendarYear"
                ],
                "nearest": true,
                "on": "pointerover",
                "clear": "pointerout"
              }
            }
          ],
          "mark": {"type": "rule"},
          "encoding": {
            "x": {
              "field": "CalendarYear",
              "type": "quantitative"
            },
            "y": {},
            "opacity": {
              "condition": {
                "value": 1,
                "param": "hover",
                "empty": false
              },
              "value": 0
            },
            "tooltip": [
              {
                "field": "CalendarYear",
                "type": "quantitative",
                "format": ""
              },
              {
                "field": "Mean Temperature",
                "type": "quantitative",
                "format": ",.1~f"
              },
              {
                "field": "10-year Mean",
                "type": "quantitative",
                "format": ",.1~f"
              }
            ]
          }
        },
        {
          "transform": [
            {
              "filter": {
                "param": "hover",
                "empty": false
              }
            }
          ],
          "mark": {
            "type": "point",
            "filled": true,
            "opacity": 1,
            "size": 25
          }
        }
      ]
    }
  ]
}'
