Literal.Value:
'{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "description": "Line chart showing annual mean temperature and 10-year rolling mean.",

  "data": {"name": "dataset"}, // input dataset reference

  // TRANSFORM SECTION
  "transform": [
    {
      // WINDOW TRANSFORM (compute 10-year rolling mean)
      "window": [
        {
          "field": "Mean Temperature", // numeric source field
          "op": "mean", // averaging function
          "as": "10-year Mean" // output field for rolling mean
        }
      ],
      "frame": [-9, 0] // rolling window covering the current year + 9 previous years
    },
    {
      // FOLD TRANSFORM (reshape from wide → long)
      // allows both series to be plotted using a single color encoding
      "fold": ["Mean Temperature", "10-year Mean"],
      "as": ["Measure", "Value"] // key–value pair columns
    }
  ],

  // GLOBAL ENCODING (shared across layers)
  "encoding": {
    "x": {
      "field": "CalendarYear", // year on x-axis
      "type": "quantitative", // treated as numeric sequence
      "axis": {
        "labels": false, // hides x-axis labels (cleaner look)
        "format": "#", // display as integer year (e.g., 1990)
        "formatType": "pbiFormat" // Power BI formatting option
      }
    },
    "y": {
      "field": "Mean Temperature", // base quantitative field
      "type": "quantitative",
      "axis": {
        "labelFont": "DIN", // custom font for axis labels
        "labelFontSize": 16,
        "tickCount": 3, // simplified y-axis tick marks
        "gridOpacity": 0.3 // light gridlines for readability
      },
      "scale": {"zero": false} // remove zero baseline (fits natural data range)
    },
    "color": {
      // distinguishes between "Mean Temperature" and "10-year Mean"
      "field": "Measure",
      "type": "nominal", // categorical color mapping
      "scale": {
        "domain": ["Mean Temperature", "10-year Mean"], // fixed order of series
        "range": ["#B3B3B3", "#404040"] // grey and dark grey tones
      },
      "legend": {
        "disable": true, // hides legend (common in Power BI custom visuals)
        "title": "Measure",
        "orient": "top-right", // legend position if enabled
        "labelFont": "DIN",
        "labelFontSize": 12,
        "titleFont": "DIN",
        "titleFontSize": 14
      }
    },
    // TOOLTIP CHANNEL (supplementary encoding)
    "tooltip": [
      {"field": "CalendarYear"},
      {
        "field": "Mean Temperature",
        "type": "quantitative",
        "format": ",.1~f" // numeric with one decimal, no trailing zeros
      },
      {
        "field": "10-year Mean",
        "type": "quantitative",
        "format": ",.1~f"
      }
    ]
  },

  // LAYERED STRUCTURE
  "layer": [
    {
      // LAYER 1: shared y encoding, applies to both the line and hover layers below
      "encoding": {
        "y": {
          "field": "Value", // plot folded "Value" column
          "type": "quantitative",
          "scale": {"zero": false}
        }
      },
      "height": 200, // overall chart height

      // nested layers: one for line, one for hover interaction
      "layer": [
        {
          // LAYER 1a: base line mark (plots both series)
          "mark": {
            "type": "line",
            "strokeWidth": 1, // line thickness
            "interpolate": "monotone" // smooth curve through points
          }
        },
        {
          // LAYER 1b: interactive rule (vertical hover line)
          "params": [
            {
              "name": "hover", // parameter used to store hovered point
              "select": {
                "type": "point",
                "fields": ["CalendarYear"], // trigger selection by x-value (year)
                "nearest": true, // snap to closest point
                "on": "pointerover", // activates when pointer hovers
                "clear": "pointerout" // clears when pointer leaves
              }
            }
          ],
          "mark": {"type": "rule"}, // vertical line shown on hover
          "encoding": {
            "x": {
              "field": "CalendarYear",
              "type": "quantitative"
            },
            "y": {}, // not needed, line extends full height by default
            "opacity": {
              // toggle line visibility when hovered
              "condition": {
                "value": 1,
                "param": "hover",
                "empty": false
              },
              "value": 0
            },
            // tooltips shown on hover (merged series values)
            "tooltip": [
              {
                "field": "CalendarYear",
                "type": "quantitative",
                "format": ""
              },
              {
                "field": "Mean Temperature",
                "type": "quantitative",
                "format": ",.1~f"
              },
              {
                "field": "10-year Mean",
                "type": "quantitative",
                "format": ",.1~f"
              }
            ]
          }
        },
        {
          // LAYER 1c: point markers at hovered year
          "transform": [
            {
              // filter to show only when a hover point exists
              "filter": {
                "param": "hover",
                "empty": false
              }
            }
          ],
          "mark": {
            "type": "point",
            "filled": true, // solid circle marker
            "opacity": 1,
            "size": 25 // fixed marker size
          }
        }
      ]
    }
  ]
}'
