'{
  "data": {"name": "dataset"}, // Connects to the Power BI data model input

  // ───────────────────────────────
  // LAYERED CHART STRUCTURE
  // ───────────────────────────────
  "layer": [

    // ╭────────────────────────────╮
    // │ LAYER 1: Main Temperature Bars │
    // ╰────────────────────────────╯
    {
      "mark": {
        "type": "bar",      // Draws horizontal bars
        "opacity": 1,       // Full opacity to emphasize values
        "tooltip": true     // Enables interactive tooltips in Power BI
      },
      "encoding": {
        "x": {
          "field": "MeanTemp",        // Binds horizontal axis to temperature
          "axis": {"title": null}     // Removes axis title for a clean design
        },
        "color": {
          // Uses a perceptually uniform multi-hue color gradient
          "scale": {
            "scheme": "spectral"      // Built-in Vega color scheme (rainbow-like)
          },
          "field": "MeanTemp",        // Drives color scale based on numeric value
          "type": "quantitative",     // Continuous quantitative color encoding

          // Custom gradient legend configuration
          "legend": {
            "autosize": "fit",              // Scales legend automatically
            "type": "gradient",             // Continuous (not discrete) legend
            "tickCount": 10,                // 10 evenly spaced tick labels
            "titleAlign": "center",         // Centers the title text
            "titleBaseline": "top",         // Aligns title to the top edge
            "titleAnchor": "center",        // Centers the title anchor point
            "title": "",                    // No visible title label
            "titlePadding": 2,              // Minimal space between title & scale
            "gradientLength": 360,          // Horizontal gradient length (in px)
            "gradientOpacity": 0.5,         // Adds semi-transparency to legend bar
            "gradientStrokeColor": "black", // Thin border color for the legend
            "gradientStrokeWidth": 0.5,     // Border thickness
            "gradientThickness": 10,        // Height (thickness) of gradient bar
            "labelOffset": 5,               // Space between labels and gradient
            "format": ".3~s",               // Short numeric format (e.g. 1.23k)
            "orient": "top",                // Legend placed above the chart
            "offset": 5,                    // Small spacing offset from top
            "direction": "horizontal",      // Horizontal gradient orientation
            "fillColor": "transparent"      // No legend background fill
          }
        }
      }
    },

    // ╭────────────────────────────╮
    // │ LAYER 2: City Labels │
    // ╰────────────────────────────╯
    {
      "mark": {
        "type": "text",      // Adds text labels to the bars
        "align": "left",     // Left-aligns labels along the start of bars
        "opacity": 1,
        "color": {
          // Dynamic label contrast:
          // Uses the Vega `luminance()` function to ensure text remains legible
          // If the bar’s background color is bright, render text in black;
          // otherwise, render it in white for contrast.
          "expr": "luminance(scale(''color'', datum.MeanTemp)) > 0.5 ? ''black'' : ''white''"
        },
        "tooltip": true,     // Allows hovering over labels for more details
        "dx": 5              // Horizontal text offset for spacing from left edge
      },
      "encoding": {
        "x": {"datum": 0},   // Positions all labels at the chart origin (x=0)
        "text": {"field": "City"} // Displays the city name from data
      }
    }
  ],

  // ───────────────────────────────
  // GLOBAL ENCODING CONFIGURATION
  // ───────────────────────────────
  "encoding": {
    "y": {
      "field": "City",             // Vertical axis represents city names
      "type": "nominal",           // Categorical field (non-numeric)
      "axis": null,                // Hides Y-axis ticks and labels
      "sort": {
        "field": "MeanTemp",       // Sorts cities by temperature
        "op": "sum",               // Uses sum operation for sorting logic
        "order": "descending"      // Displays highest temperature first
      }
    },
    "x": {
      "type": "quantitative",      // Continuous numeric x-axis
      "axis": {"title": "MeanTemp"}// Axis title visible under the chart
    }
  }
}'
