'{
  "data": {"name": "dataset"}, // Data source reference (Power BI model binding)

  // ───────────────────────────────
  // LAYERED STRUCTURE
  // ───────────────────────────────
  "layer": [

    // ╭────────────────────────────╮
    // │ LAYER 1: Horizontal Bars (Color Gradient) │
    // ╰────────────────────────────╯
    {
      "mark": {
        "type": "bar",       // Base mark type
        "opacity": 1,        // Fully opaque for visual clarity
        "tooltip": true      // Enables hover tooltips in Power BI
      },
      "encoding": {
        "x": {
          "field": "MeanTemp",        // Encodes temperature as X position
          "axis": {"title": null}     // No axis title (clean KPI aesthetic)
        },
        "color": {
          // Quantitative color mapping using the sinebow scheme
          "scale": {
            "scheme": "sinebow"       // Continuous rainbow-like gradient (cyclic hue)
          },
          "field": "MeanTemp",        // Maps color directly to temperature values
          "type": "quantitative",     // Continuous data scale

          // Gradient legend settings
          "legend": {
            "autosize": "fit",              // Auto-adjust legend dimensions
            "type": "gradient",             // Continuous gradient legend
            "tickCount": 10,                // Approx. 10 labeled intervals
            "titleAlign": "center",         // Centered legend title
            "titleBaseline": "top",         // Title placed above the gradient
            "titleAnchor": "center",        // Keeps title text centered
            "title": "",                    // No explicit title (minimal design)
            "titlePadding": 2,              // Small padding under title
            "gradientLength": 360,          // Total length (horizontal) in px
            "gradientOpacity": 0.5,         // Adds transparency for subtlety
            "gradientStrokeColor": "black", // Outline stroke for legend gradient
            "gradientStrokeWidth": 0.5,     // Thin stroke width
            "gradientThickness": 10,        // Vertical thickness of gradient strip
            "labelOffset": 5,               // Space between tick labels and gradient
            "format": ".3~s",               // Short SI number formatting (1.23k)
            "orient": "top",                // Places legend above chart
            "offset": 5,                    // Distance offset from top edge
            "direction": "horizontal",      // Horizontal gradient layout
            "fillColor": "transparent"      // Transparent legend background
          }
        }
      }
    },

    // ╭────────────────────────────╮
    // │ LAYER 2: Text Labels (City Names) │
    // ╰────────────────────────────╯
    {
      "mark": {
        "type": "text",       // Adds city labels beside bars
        "align": "left",      // Left-aligned text
        "opacity": 1,         // Fully visible
        "color": {
          // Uses luminance contrast detection for readability
          // If background (from color scale) is bright → black text
          // Else → white text for contrast
          "expr": "luminance(scale(''color'', datum.MeanTemp)) > 0.5 ? ''black'' : ''white''"
        },
        "tooltip": true,      // Tooltip-enabled labels
        "dx": 5               // Small horizontal offset from chart edge
      },
      "encoding": {
        "x": {"datum": 0},    // Positions text at the start of each bar
        "text": {"field": "City"} // Displays city name dynamically
      }
    }
  ],

  // ───────────────────────────────
  // GLOBAL ENCODING (Axes & Sorting)
  // ───────────────────────────────
  "encoding": {
    "y": {
      "field": "City",             // Vertical axis defines category grouping
      "type": "nominal",           // Non-numeric categorical values
      "axis": null,                // Hides axis and tick marks
      "sort": {
        "field": "MeanTemp",       // Sorts by total temperature
        "op": "sum",               // Aggregates if duplicates exist
        "order": "descending"      // Highest temperatures at top
      }
    },
    "x": {
      "type": "quantitative",      // Numeric axis for temperature values
      "axis": {"title": "MeanTemp"}// Label for x-axis metric
    }
  }
}'
