'{
  "data": {"name": "dataset"}, // Data source name, automatically bound in Power BI (dynamic dataset context)

  // ───────────────────────────────
  // LAYERED VISUAL STRUCTURE
  // ───────────────────────────────
  "layer": [

    // ╭────────────────────────────╮
    // │ LAYER 1: Divergent Gradient Bars │
    // ╰────────────────────────────╯
    {
      "mark": {
        "type": "bar",       // Base bar shape
        "opacity": 1,        // Fully opaque for solid appearance
        "tooltip": true      // Enables interactivity tooltips
      },
      "encoding": {
        "x": {
          "field": "MeanTemp",        // Encodes numerical value (temperature)
          "axis": {"title": null}     // Suppresses default x-axis title for compact layout
        },
        "color": {
          "scale": {
            "scheme": "pbiColorDivergent" // Diverging Power BI color scale (neutral midpoint)
          },
          "field": "MeanTemp",             // Maps bar color intensity to temperature magnitude
          "type": "quantitative",          // Continuous numeric data scale

          // ─────────────
          // LEGEND CONFIG
          // ─────────────
          "legend": {
            "autosize": "fit",              // Automatically scales legend size to fit
            "type": "gradient",             // Continuous gradient legend
            "tickCount": 10,                // Approximate number of labeled ticks
            "titleAlign": "center",         // Centers legend title
            "titleBaseline": "top",         // Places title above gradient bar
            "titleAnchor": "center",        // Centers horizontally
            "title": "",                    // No text (minimal design)
            "titlePadding": 2,              // Spacing under title
            "gradientLength": 360,          // Width of gradient legend
            "gradientOpacity": 0.5,         // Transparent gradient for visual softness
            "gradientStrokeColor": "black", // Outline color for gradient track
            "gradientStrokeWidth": 0.5,     // Thin border around legend
            "gradientThickness": 10,        // Vertical thickness (height) of gradient bar
            "labelOffset": 5,               // Distance between labels and gradient
            "format": ".3~s",               // Uses SI-prefix (e.g. 1.23k)
            "orient": "top",                // Positions legend above chart
            "offset": 5,                    // Slight padding above the visual
            "direction": "horizontal",      // Gradient flows left→right
            "fillColor": "transparent"      // Transparent legend background (integrates with report theme)
          }
        }
      }
    },

    // ╭────────────────────────────╮
    // │ LAYER 2: Text Labels (City Names) │
    // ╰────────────────────────────╯
    {
      "mark": {
        "type": "text",       // Overlay text labels for category names
        "align": "left",      // Left-aligns labels near start of bars
        "opacity": 1,         // Fully visible
        "color": {
          // Dynamically adjusts label color for accessibility:
          // Uses luminance of the bar’s fill color to ensure contrast
          "expr": "luminance(scale(''color'', datum.MeanTemp)) > 0.5 ? ''black'' : ''white''"
        },
        "tooltip": true,      // Tooltips for labels (same context as bars)
        "dx": 5               // Small offset to separate text from bar edge
      },
      "encoding": {
        "x": {"datum": 0},    // Aligns text at x=0 (start of bar)
        "text": {"field": "City"} // Uses the "City" field for label content
      }
    }
  ],

  // ───────────────────────────────
  // GLOBAL ENCODING SETTINGS
  // ───────────────────────────────
  "encoding": {
    "y": {
      "field": "City",             // Defines categorical axis
      "type": "nominal",           // Treats city names as discrete categories
      "axis": null,                // Removes y-axis labels and ticks (clean look)
      "sort": {
        "field": "MeanTemp",       // Sorts cities by average temperature
        "op": "sum",               // Aggregation (if multiple rows per city)
        "order": "descending"      // Highest temperature first (top-down order)
      }
    },
    "x": {
      "type": "quantitative",      // Numeric x-axis
      "axis": {"title": "MeanTemp"}// Displays temperature metric label
    }
  }
}'
