'{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",

  "data": {"name": "dataset"}, // bound dataset (Power BI or external input)

  // PARAMS (constants used for scaling, color, and layout)
  "params": [
    {"name": "bwActual", "value": 0.8}, // bar width (Actual)
    {"name": "bwBudget", "value": 0.8}, // bar width (Budget)
    {"name": "bwPlan", "value": 0.8},   // bar width (Plan)
    {"name": "bwDelta", "value": 0.15}, // bar width (Delta comparison)

    {"name": "_strokeColour", "value": "black"}, // border stroke color
    {"name": "_actualColour", "value": "navy"},   // main Actual bar color
    {"name": "_forecastColour", "value": "#f0eee9"}, // forecast bar color (light)
    {"name": "_forecastPlanColour", "value": "url(#diagonal-stripe-1-30)"}, // patterned forecast fill
    {"name": "_planColour", "value": "white"}, // plan bar color

    {"name": "textMultiplier", "value": 4.5}, // scales axis label font
    {"name": "labelMultiplier", "value": 4},  // scales data label font
    {"name": "_deltaOffsetNEG", "value": 1.1} // offset control for negative variance
  ],

  // TRANSFORM (derives helper fields and contextual variables)
  "transform": [
    {
      // compute global min and max for AC (used in highlight logic)
      "window": [
        {"op": "max", "field": "AC", "as": "max_AC"},
        {"op": "min", "field": "AC", "as": "min_AC"}
      ],
      "frame": [null, null]
    },
    {"calculate": "now()", "as": "_now"}, // current time reference
    {"calculate": "datetime( now())", "as": "_datetime"}, // current datetime object
    {
      "calculate": "year(utcFormat(datetime( now()), ''%d-%b-%y''))",
      "as": "_currentYearUtc" // extract year for potential logic use
    },
    {
      "calculate": "utcFormat(datetime( now()), ''%d-%b-%y'')",
      "as": "_utcFormat" // formatted string representation of today
    },
    {
      // hardcoded “today” for consistent comparison logic
      "name": "_DATE_TODAY",
      "calculate": "datetime(2024, 5, 1)", // June 1, 2024 (month index 5)
      "as": "_dateToday"
    },
    {
      // binary comparison of data date vs. hardcoded today
      "name": "_DATE_TEST",
      "calculate": "datum._dateToday > datum[''Date''] ? 0 : 1 ",
      "as": "_dateTest" // 0 = past (Actual), 1 = future (Forecast)
    },
    {
      "calculate": "datum[''AC''] - datum[''PL'']",
      "as": "__varianceToPlan" // difference between Actual and Plan
    },
    {
      "calculate": "datum.__varianceToPlan > 0 ? -1: 1",
      "as": "__deltaOffsetMultiplier" // directional multiplier for label offsets
    },
    {
      "calculate": "datum.__varianceToPlan > 0 ? 1.4 : 1.99",
      "as": "__deltaOffsetDivider" // divider scaling based on variance sign
    },
    {
      "calculate": "(( datum[''AC''] - datum[''PL''] ) / datum[''PL''])",
      "as": "__variancePCT" // percent variance vs. Plan
    },
    {
      "calculate": "(bwDelta-(bwDelta*2))",
      "as": "_deltaWidth" // derived delta bar width
    },
    {
      "calculate": "(bwPlan - bwDelta)",
      "as": "_offsetPlanDelta" // x-offset for delta alignment
    },
    {
      "calculate": "datum.__variancePCT > 0 ? -2 : 1",
      "as": "_deltaOffset" // offset for text or delta positioning
    },
    {
      // delta color selection (green if positive, red otherwise)
      "calculate": "datum[''AC''] > datum[''PL''] ? ''#c3f550'' : ''tomato''",
      "as": "_deltaColourFormat"
    },
    {
      // stroke color based on delta direction
      "calculate": "datum[''AC''] > datum[''PL''] ? _strokeColour : ''black''",
      "as": "_deltaStrokeFormat"
    },
    {
      // global offset used for alignment
      "calculate": "(bwPlan) * -0.169",
      "as": "_globalOffset"
    }
  ],

  // GLOBAL ENCODING (applies across all layers)
  "encoding": {
    "x": {
      "field": "Date", // x-axis field
      "type": "nominal", // treated as categories
      "axis": {
        "title": null, // no x-axis title
        "domain": true, // show axis baseline
        "labels": true, // show tick labels
        "formatType": "pbiFormat", // Power BI-style formatting
        "format": "MMM-yy", // month-year format
        "labelAngle": 0, // horizontal labels
        "labelAlign": "center",
        "labelPadding": 5,
        "labelOffset": 0,
        // dynamic scaling of label font size
        "labelFontSize": {
          "expr": " (bandwidth(''x'') / textMultiplier ) * bwActual+3"
        }
      }
    },
    "y": {
      "field": "AC", // Actual value for y-position
      "type": "quantitative",
      "axis": null, // hide y-axis elements
      "scale": {"zero": false} // non-zero baseline scaling
    },
    "color": {
      // global color encoding used for highlights and forecasting
      "condition": [
        {
          "test": "datum.AC === datum.max_AC",
          "value": "limegreen" // highlight maximum AC value
        },
        {
          "test": "datum.AC === datum.min_AC",
          "value": "red" // highlight minimum AC value
        },
        {
          "test": "datum._dateTest == 1",
          "value": "whitesmoke" // forecasted (future) values
        }
      ],
      "value": "navy" // default (past) Actual bar color
    }
  },

  "description": "_LAYER_", // document grouping or reference label

  // LAYERS (rendered in order)
  "layer": [
    {
      "description": "ACTUAL_BAR", // primary bar for Actual and Forecast values
      "mark": {
        "type": "bar",
        "width": {
          "expr": "(bandwidth(''x'')) * (bwActual )" // scaled bar width
        },
        // conditional color fill by date test
        "color": {
          "expr": "datum._dateTest == 0 ? _actualColour : _forecastColour"
        },
        "stroke": {
          "expr": "_strokeColour" // consistent stroke border
        },
        "strokeWidth": 0.6 // thin outline for visual separation
      }
    },
    {
      "description": "ACTUAL_BAR", // duplicate bar (may support layering or effect)
      "mark": {
        "type": "bar",
        "width": {
          "expr": "(bandwidth(''x'')) * (bwActual )"
        },
        "stroke": {
          "expr": "_strokeColour"
        },
        "strokeWidth": 0.6
      }
    },
    {
      "description": "ACTUAL_DATA_LABELS", // overlayed value labels
      "mark": {
        "type": "text",
        // conditional label color (white on dark past bars, black on light forecast bars)
        "color": {
          "expr": "datum._dateTest == 0 ? ''white'' : ''black''"
        },
        "fontSize": {
          "expr": " ( bandwidth(''x'')/ labelMultiplier ) * bwActual" // responsive label sizing
        },
        "yOffset": 3, // nudge text down slightly
        "xOffset": -4, // small horizontal shift
        "baseline": "top", // anchor to bar top
        "align": "left", // align text left of mark
        "fontWeight": "bold" // improve legibility
      },
      "encoding": {
        "text": {
          "field": "AC", // label uses Actual value
          "type": "quantitative",
          "format": ".2s" // compact SI formatting
        },
        "color": {
          // highlight label color on min/max bars
          "condition": [
            {"test": "datum.AC === datum.max_AC", "value": "white"},
            {"test": "datum.AC === datum.min_AC", "value": "white"},
            {"test": "datum._dateTest == 1", "value": "black"}
          ],
          "value": "white" // default white for dark Actual bars
        }
      }
    }
  ]
}'
