Literal.Value:
'{
  "data": {"name": "dataset"},
  "params": [
    {"name": "bwActual", "value": 0.8},
    {"name": "bwBudget", "value": 0.8},
    {"name": "bwPlan", "value": 0.8},
    {"name": "bwDelta", "value": 0.15},
    {
      "name": "_strokeColour",
      "value": "black"
    },
    {
      "name": "_actualColour",
      "value": "navy"
    },
    {
      "name": "_forecastColour",
      "value": "#f0eee9"
    },
    {
      "name": "_forecastPlanColour",
      "value": "url(#diagonal-stripe-1-30)"
    },
    {
      "name": "_planColour",
      "value": "white"
    },
    {
      "name": "textMultiplier",
      "value": 4.5
    },
    {
      "name": "labelMultiplier",
      "value": 4
    },
    {
      "name": "_deltaOffsetNEG",
      "value": 1.1
    }
  ],
  "transform": [
    {
      "window": [
        {
          "op": "max",
          "field": "AC",
          "as": "max_AC"
        },
        {
          "op": "min",
          "field": "AC",
          "as": "min_AC"
        }
      ],
      "frame": [null, null]
    },
    {
      "calculate": "now()",
      "as": "_now"
    },
    {
      "calculate": "datetime( now())",
      "as": "_datetime"
    },
    {
      "calculate": "year(utcFormat(datetime( now()), ''%d-%b-%y''))",
      "as": "_currentYearUtc"
    },
    {
      "calculate": "utcFormat(datetime( now()), ''%d-%b-%y'')",
      "as": "_utcFormat"
    },
    {
      "name": "_DATE_TODAY",
      "calculate": "datetime( now()) ",
      "as": "_dateToday"
    },
    {
      "name": "_DATE_TEST",
      "calculate": "datum._dateToday > datum[''Date''] ? 0 : 1 ",
      "as": "_dateTest"
    },
    {
      "calculate": "datum[''AC''] - datum[''PL'']",
      "as": "__varianceToPlan"
    },
    {
      "calculate": "datum.__varianceToPlan > 0 ? -1: 1",
      "as": "__deltaOffsetMultiplier"
    },
    {
      "calculate": "datum.__varianceToPlan > 0 ? 1.4 : 1.99",
      "as": "__deltaOffsetDivider"
    },
    {
      "calculate": "(( datum[''AC''] - datum[''PL''] ) / datum[''PL''])",
      "as": "__variancePCT"
    },
    {
      "calculate": "(bwDelta-(bwDelta*2))",
      "as": "_deltaWidth"
    },
    {
      "calculate": "(bwPlan - bwDelta)",
      "as": "_offsetPlanDelta"
    },
    {
      "calculate": "datum.__variancePCT > 0 ? -2 : 1",
      "as": "_deltaOffset"
    },
    {
      "calculate": "datum[''AC''] > datum[''PL''] ? ''#c3f550'' : ''tomato''",
      "as": "_deltaColourFormat"
    },
    {
      "calculate": "datum[''AC''] > datum[''PL''] ? _strokeColour : ''black''",
      "as": "_deltaStrokeFormat"
    },
    {
      "calculate": "(bwPlan) * -0.169",
      "as": "_globalOffset"
    }
  ],
"encoding": {
  "x": {
    "field": "Date",
    "type": "nominal",
    "axis": {
      "title": null,
      "domain": true,
      "labels": true,
      "formatType": "pbiFormat",
      "format": "MMM-yy",
      "labelAngle": 0,
      "labelAlign": "center",
      "labelPadding": 5,
      "labelOffset": 0,
      "labelFontSize": {
        "expr": " (bandwidth(''x'') / textMultiplier ) * bwActual+3"
      }
    }
  },
  "y": {
    "field": "AC",
    "type": "quantitative",
    "axis": null,
    "scale": {"zero": false}
  },
  "color": {
    "condition": [
      {
        "test": "datum.AC === datum.max_AC",
        "value": "limegreen"
      },
      {
        "test": "datum.AC === datum.min_AC",
        "value": "red"
      },
      {
        "test": "datum._dateTest == 1",
        "value": "whitesmoke"
      }
    ],
    "value": "navy"
  }
},
  "description": "_LAYER_",
  "layer": [
    {
      "description": "ACTUAL_BAR",
      "mark": {
        "type": "bar",
        "width": {
          "expr": "(bandwidth(''x'')) * (bwActual )"
        },
        "color": {
          "expr": "datum._dateTest == 0 ? _actualColour : _forecastColour"
        },
        "stroke": {
          "expr": "_strokeColour"
        },
        "strokeWidth": 0.6
      }
    },
    {
      "description": "ACTUAL_BAR",
      "mark": {
        "type": "bar",
        "width": {
          "expr": "(bandwidth(''x'')) * (bwActual )"
        },
        "stroke": {
          "expr": "_strokeColour"
        },
        "strokeWidth": 0.6
      }
    },
    {
      "description": "ACTUAL_DATA_LABELS",
      "mark": {
        "type": "text",
        "color": {
          "expr": "datum._dateTest == 0 ? ''white'' : ''black''"
        },
        "fontSize": {
          "expr": " ( bandwidth(''x'')/ labelMultiplier ) * bwActual"
        },
        "yOffset": 3,
        "xOffset": -4,
        "baseline": "top",
        "align": "left",
        "fontWeight": "bold"
      },
      "encoding": {
        "text": {
          "field": "AC",
          "type": "quantitative",
          "format": ".2s"
        },
        "color": {
          "condition": [
            {
              "test": "datum.AC === datum.max_AC",
              "value": "white"
            },
            {
              "test": "datum.AC === datum.min_AC",
              "value": "white"
            },
            {
              "test": "datum._dateTest == 1",
              "value": "black"
            }
          ],
          "value": "white"
        }
      }
    }
  ]
}'
