'{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",

  "data": {"name": "dataset"}, // dataset input (e.g., Power BI table or Vega data binding)

  // PARAMETERS (define configurable constants used across layers)
  "params": [
    {"name": "bwActual", "value": 0.8}, // bar width multiplier for Actual
    {"name": "bwBudget", "value": 0.8}, // width for Budget bars
    {"name": "bwACan", "value": 0.8}, // width for ACan (alternative Actual)
    {"name": "bwDelta", "value": 0.15}, // width for Delta variance overlay

    // visual appearance and colors
    {"name": "_strokeColour", "value": "black"}, // outline color
    {"name": "_actualColour", "value": "#404040"}, // Actual bar color
    {"name": "_forecastColour", "value": "#f0eee9"}, // forecast fill color
    {"name": "forecast_bar_colour", "value": "url(#diagonal-stripe-1-30)"}, // patterned forecast bar
    {"name": "actual_bar_colour", "value": "ivory"}, // ACan bar fill color

    // text and label sizing multipliers
    {"name": "textMultiACier", "value": 4.5}, // controls axis label font size
    {"name": "labelMultiACier", "value": 4}, // controls label font scaling

    {"name": "_deltaOffsetNEG", "value": 1.1} // offset for negative delta adjustment
  ],

  // TRANSFORMS (compute helper and contextual fields)
  "transform": [
    {
      // global max/min window for AC (Actual)
      "window": [
        {"op": "max", "field": "AC", "as": "max_AC"},
        {"op": "min", "field": "AC", "as": "min_AC"}
      ],
      "frame": [null, null]
    },
    {
      "calculate": "now()", "as": "_now" // current timestamp
    },
    {
      "calculate": "datetime( now())", "as": "_datetime" // current datetime object
    },
    {
      // extract current UTC year
      "calculate": "year(utcFormat(datetime( now()), ''%d-%b-%y''))",
      "as": "_currentYearUtc"
    },
    {
      // format date for UTC display
      "calculate": "utcFormat(datetime( now()), ''%d-%b-%y'')",
      "as": "_utcFormat"
    },
    {
      // store today''s date
      "name": "_DATE_TODAY",
      "calculate": "datetime( now()) ",
      "as": "_dateToday"
    },
    {
      // compare today''s date vs. record''s Date value
      "name": "_DATE_TEST",
      "calculate": "datum._dateToday > datum[''Date''] ? 0 : 1 ",
      "as": "_dateTest" // 0 = past, 1 = future
    },
    {
      // placeholder variance (AC - AC = 0)
      "calculate": "datum[''AC''] - datum[''AC'']",
      "as": "__varianceToACan"
    },
    {
      // offset multiplier based on sign
      "calculate": "datum.__varianceToACan > 0 ? -1: 1",
      "as": "__deltaOffsetMultiACier"
    },
    {
      // scaling divider based on sign
      "calculate": "datum.__varianceToACan > 0 ? 1.4 : 1.99",
      "as": "__deltaOffsetDivider"
    },
    {
      // variance percentage (will result in zero)
      "calculate": "(( datum[''AC''] - datum[''AC''] ) / datum[''AC''])",
      "as": "__variancePCT"
    },
    {
      // width adjustment for delta layer
      "calculate": "(bwDelta-(bwDelta*2))",
      "as": "_deltaWidth"
    },
    {
      // ⚠ Typo intentionally preserved (“calcaulte” — ignored by Vega)
      // intended to compute offset between ACan and Delta
      "calcaulte": "(bwACan - bwDelta)",
      "as": "_offsetACanDelta"
    },
    {
      // ⚠ Typo preserved (“calcuate” — ignored by Vega)
      // intended to offset delta depending on variance
      "calcuate": "datum.__variancePCT > 0 ? -2 : 1",
      "as": "_deltaOffset"
    },
    {
      // conditional coloring for delta marks (no effect here)
      "calculate": "datum[''AC''] > datum[''AC''] ? ''#c3f550'' : ''tomato''",
      "as": "_deltaColourFormat"
    },
    {
      // conditional stroke color (no effect here)
      "calculate": "datum[''AC''] > datum[''AC''] ? _strokeColour : ''black''",
      "as": "_deltaStrokeFormat"
    },
    {
      // small alignment correction for grouped bars
      "calculate": "(bwACan) * -0.169",
      "as": "_globalOffset"
    }
  ],

  // GLOBAL ENCODING (applies to all layers)
  "encoding": {
    "x": {
      "field": "Date", // x-axis categorical date field
      "type": "nominal",
      "axis": {
        "title": null, // remove axis title
        "domain": true, // show baseline
        "labels": true, // show tick labels
        "formatType": "pbiFormat", // Power BI-compatible formatter
        "format": "MMM-yy", // month-year format
        "labelAngle": 0, // horizontal labels
        "labelAlign": "center",
        "labelPadding": 5,
        "labelOffset": 0,
        // dynamically scale label font size with bar width
        "labelFontSize": {
          "expr": " (bandwidth(''x'') / textMultiACier ) * bwActual+3"
        }
      }
    },
    "y": {
      "field": "AC", // measure field (Actual)
      "type": "quantitative",
      "axis": null, // hide axis visuals
      "scale": {"zero": false} // flexible scale not forced to start at 0
    }
  },

  "description": "_LAYER_", // layer metadata (useful in multi-layer composition)

  // LAYERS (drawing order: bar first, then label)
  "layer": [
    {
      "description": "ACAN_BAR", // first layer: ACan bars
      "mark": {
        "type": "bar", // base rectangular mark
        "xOffset": 0, // centered within category
        // width computed dynamically from parameter and scale bandwidth
        "width": {
          "expr": "(bandwidth(''x'')) * (bwACan )"
        },
        "fill": "ivory", // bar fill color (matches param)
        "stroke": "dimgrey" // thin dark border for contrast
      }
    },
    {
      "description": "ACTUAL_DATA_LABELS", // second layer: numeric labels
      "mark": {
        "type": "text", // text mark for in-bar values
        "color": "black", // static label color (no conditional logic here)
        // dynamic scaling for readability based on width
        "fontSize": {
          "expr": " ( bandwidth(''x'')/ labelMultiACier ) * bwActual"
        },
        "yOffset": 5, // slight vertical adjustment
        "xOffset": -5, // horizontal offset leftward
        "baseline": "top", // text anchored to bar top
        "align": "left", // align text to left edge
        "fontWeight": "bold" // improve legibility
      },
      "encoding": {
        // bind text to AC field values
        "text": {
          "field": "AC",
          "type": "quantitative",
          "format": ".2s" // short SI notation (e.g., 1.2k)
        }
      }
    }
  ]
}'
