Literal.Value:
'{
  "data": {"name": "dataset"},
  "params": [
    {"name": "bwActual", "value": 0.8},
    {"name": "bwBudget", "value": 0.8},
    {"name": "bwACan", "value": 0.8},
    {"name": "bwDelta", "value": 0.15},
    {
      "name": "_strokeColour",
      "value": "black"
    },
    {
      "name": "_actualColour",
      "value": "#404040"
    },
    {
      "name": "_forecastColour",
      "value": "#f0eee9"
    },
    {
      "name": "forecast_bar_colour",
      "value": "url(#diagonal-stripe-1-30)"
    },
    {
      "name": "actual_bar_colour",
      "value": "ivory"
    },
    {
      "name": "textMultiACier",
      "value": 4.5
    },
    {
      "name": "labelMultiACier",
      "value": 4
    },
    {
      "name": "_deltaOffsetNEG",
      "value": 1.1
    }
  ],
  "transform": [
    {
      "window": [
        {
          "op": "max",
          "field": "AC",
          "as": "max_AC"
        },
        {
          "op": "min",
          "field": "AC",
          "as": "min_AC"
        }
      ],
      "frame": [null, null]
    },
    {
      "calculate": "now()",
      "as": "_now"
    },
    {
      "calculate": "datetime( now())",
      "as": "_datetime"
    },
    {
      "calculate": "year(utcFormat(datetime( now()), ''%d-%b-%y''))",
      "as": "_currentYearUtc"
    },
    {
      "calculate": "utcFormat(datetime( now()), ''%d-%b-%y'')",
      "as": "_utcFormat"
    },
    {
      "name": "_DATE_TODAY",
      "calculate": "datetime( now()) ",
      "as": "_dateToday"
    },
    {
      "name": "_DATE_TEST",
      "calculate": "datum._dateToday > datum[''Date''] ? 0 : 1 ",
      "as": "_dateTest"
    },
    {
      "calculate": "datum[''AC''] - datum[''AC'']",
      "as": "__varianceToACan"
    },
    {
      "calculate": "datum.__varianceToACan > 0 ? -1: 1",
      "as": "__deltaOffsetMultiACier"
    },
    {
      "calculate": "datum.__varianceToACan > 0 ? 1.4 : 1.99",
      "as": "__deltaOffsetDivider"
    },
    {
      "calculate": "(( datum[''AC''] - datum[''AC''] ) / datum[''AC''])",
      "as": "__variancePCT"
    },
    {
      "calculate": "(bwDelta-(bwDelta*2))",
      "as": "_deltaWidth"
    },
    {
      "calcaulte": "(bwACan - bwDelta)",
      "as": "_offsetACanDelta"
    },
    {
      "calcuate": "datum.__variancePCT > 0 ? -2 : 1",
      "as": "_deltaOffset"
    },
    {
      "calculate": "datum[''AC''] > datum[''AC''] ? ''#c3f550'' : ''tomato''",
      "as": "_deltaColourFormat"
    },
    {
      "calculate": "datum[''AC''] > datum[''AC''] ? _strokeColour : ''black''",
      "as": "_deltaStrokeFormat"
    },
    {
      "calculate": "(bwACan) * -0.169",
      "as": "_globalOffset"
    }
  ],
  "encoding": {
    "x": {
      "field": "Date",
      "type": "nominal",
      "axis": {
        "title": null,
        "domain": true,
        "labels": true,
        "formatType": "pbiFormat",
        "format": "MMM-yy",
        "labelAngle": 0,
        "labelAlign": "center",
        "labelPadding": 5,
        "labelOffset": 0,
        "labelFontSize": {
          "expr": " (bandwidth(''x'') / textMultiACier ) * bwActual+3"
        }
      }
    },
    "y": {
      "field": "AC",
      "type": "quantitative",
      "axis": null,
      "scale": {"zero": false}
    }
  },
  "description": "_LAYER_",
  "layer": [
    {
      "description": "ACAN_BAR",
      "mark": {
        "type": "bar",
        "xOffset": 0,
        "width": {
          "expr": "(bandwidth(''x'')) * (bwACan )"
        },
        "fill": "ivory",
        "stroke": "dimgrey"
      }
    },
    {
      "description": "ACTUAL_DATA_LABELS",
      "mark": {
        "type": "text",
        "color": "black",
        "fontSize": {
          "expr": " ( bandwidth(''x'')/ labelMultiACier ) * bwActual"
        },
        "yOffset": 5,
        "xOffset": -5,
        "baseline": "top",
        "align": "left",
        "fontWeight": "bold"
      },
      "encoding": {
        "text": {
          "field": "AC",
          "type": "quantitative",
          "format": ".2s"
        }
      }
    }
  ]
}'
