Literal.Value:
'{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",

  "data": {"name": "dataset"}, // dataset reference (bound externally, e.g., from Power BI)

  // PARAMETERS (define constants and configuration values)
  "params": [
    {"name": "bwActual", "value": 0.8}, // bar width multiplier for Actuals
    {"name": "bwBudget", "value": 0.8}, // width multiplier for Budget bars
    {"name": "bwPlan", "value": 0.8}, // width multiplier for Plan bars
    {"name": "bwDelta", "value": 0.15}, // width multiplier for Delta overlays

    // base color parameters
    {"name": "_strokeColour", "value": "black"}, // stroke (outline) color
    {"name": "_actualColour", "value": "navy"}, // color for Actual bars
    {"name": "_forecastColour", "value": "#f0eee9"}, // color for forecast bars
    {"name": "_forecastPlanColour", "value": "url(#diagonal-stripe-1-30)"}, // pattern fill for forecast plan bars
    {"name": "_planColour", "value": "white"}, // color for Plan bars

    // text and label scaling
    {"name": "textMultiplier", "value": 4.5}, // controls date label font size
    {"name": "labelMultiplier", "value": 4}, // label spacing multiplier

    // positioning constants for delta (variance) visuals
    {"name": "_deltaOffsetNEG", "value": 1.1} // offset applied for negative deltas
  ],

  // TRANSFORMS (data calculations and variance logic)
  "transform": [
    {
      // window transform: compute global max and min of AC (Actuals)
      "window": [
        {"op": "max", "field": "AC", "as": "max_AC"},
        {"op": "min", "field": "AC", "as": "min_AC"}
      ],
      "frame": [null, null]
    },
    {
      // timestamp helpers for current time and formatted date
      "calculate": "now()", "as": "_now"
    },
    {
      "calculate": "datetime( now())", "as": "_datetime"
    },
    {
      // extracts the current UTC year as a number
      "calculate": "year(utcFormat(datetime( now()), ''%d-%b-%y''))",
      "as": "_currentYearUtc"
    },
    {
      // formats the current date in UTC using day-month-year pattern
      "calculate": "utcFormat(datetime( now()), ''%d-%b-%y'')",
      "as": "_utcFormat"
    },
    {
      // stores today''s datetime object
      "name": "_DATE_TODAY",
      "calculate": "datetime( now()) ",
      "as": "_dateToday"
    },
    {
      // compares current date with each record''s Date field
      "name": "_DATE_TEST",
      "calculate": "datum._dateToday > datum[''Date''] ? 0 : 1 ",
      "as": "_dateTest" // 0 = past, 1 = future
    },
    {
      // calculates absolute variance from Plan
      "calculate": "datum[''AC''] - datum[''PL'']",
      "as": "__varianceToPlan"
    },
    {
      // determines direction multiplier based on variance sign
      "calculate": "datum.__varianceToPlan > 0 ? -1: 1",
      "as": "__deltaOffsetMultiplier"
    },
    {
      // sets a divider used for adjusting delta offset scaling
      "calculate": "datum.__varianceToPlan > 0 ? 1.4 : 1.99",
      "as": "__deltaOffsetDivider"
    },
    {
      // computes percentage variance relative to Plan
      "calculate": "(( datum[''AC''] - datum[''PL''] ) / datum[''PL''])",
      "as": "__variancePCT"
    },
    {
      // defines the width of delta bar (negative value for centering)
      "calculate": "(bwDelta-(bwDelta*2))",
      "as": "_deltaWidth"
    },
    {
      // computes horizontal offset between Plan and Delta bars
      "calculate": "(bwPlan - bwDelta)",
      "as": "_offsetPlanDelta"
    },
    {
      // offsets delta mark depending on sign (positive or negative variance)
      "calculate": "datum.__variancePCT > 0 ? -2 : 1",
      "as": "_deltaOffset"
    },
    {
      // sets delta fill color: greenish for positive, red for negative
      "calculate": "datum[''AC''] > datum[''PL''] ? ''#c3f550'' : ''tomato''",
      "as": "_deltaColourFormat"
    },
    {
      // sets stroke color depending on variance direction
      "calculate": "datum[''AC''] > datum[''PL''] ? _strokeColour : ''black''",
      "as": "_deltaStrokeFormat"
    },
    {
      // global x-offset for bar alignment tweaks
      "calculate": "(bwPlan) * -0.169",
      "as": "_globalOffset"
    }
  ],

  // GLOBAL ENCODING (axes shared by all layers)
  "encoding": {
    "x": {
      "field": "Date", // x-axis field (categorical, e.g. month-year)
      "type": "nominal",
      "axis": {
        "title": null, // removes axis title
        "domain": true, // shows baseline
        "labels": true, // display date labels
        "formatType": "pbiFormat", // Power BI-compatible formatter
        "format": "MMM-yy", // month-year pattern
        "labelAngle": 0, // horizontal labels
        "labelAlign": "center",
        "labelPadding": 5,
        "labelOffset": 0,
        // dynamically scales label font size based on bar width
        "labelFontSize": {
          "expr": " (bandwidth(''x'') / textMultiplier ) * bwActual+3"
        }
      }
    },
    "y": {
      "field": "AC", // quantitative field for Actuals
      "type": "quantitative",
      "axis": null, // hides y-axis
      "scale": {"zero": false} // avoids forcing scale to start at zero
    }
  },

  "description": "_LAYER_", // label for this visual layer (may be part of a larger composition)

  // LAYER DEFINITIONS (marks drawn in rendering order)
  "layer": [
    {
      "description": "ACTUAL_BAR", // layer 1: Actual values as bars
      "mark": {
        "type": "bar", // rectangular bar mark
        // dynamically sets bar width using param and band scale
        "width": {
          "expr": "(bandwidth(''x'')) * (bwActual )"
        },
        "strokeWidth": 0.6 // outline width for bar edges
      }
    }
  ]
}'
