'{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",

  "data": {"name": "dataset"}, // (external dataset reference, Power BI or JSON feed)

  // ──────────────────────────────
  // PARAMS: global configuration constants
  // ──────────────────────────────
  "params": [
    {"name": "bwActual", "value": 0.8},   // (bar width - Actual)
    {"name": "bwBudget", "value": 0.8},   // (bar width - Budget)
    {"name": "bwACan", "value": 0.8},     // (bar width - Actual Canonical)
    {"name": "bwDelta", "value": 0.15},   // (bar width - Delta overlay)
    {"name": "_strokeColour", "value": "black"},  // (default stroke)
    {"name": "_actualColour", "value": "#404040"}, // (primary Actual bar fill)
    {"name": "_forecastColour", "value": "#f0eee9"}, // (Forecast bar fill)
    {"name": "_forecast_colour", "value": "url(#diagonal-stripe-1-30)"}, // (pattern fill)
    {"name": "_actual_colour", "value": "ivory"}, // (Actual fill when past date)
    {"name": "textMultiACier", "value": 4.5}, // (x-axis label scaling multiplier)
    {"name": "labelMultiACier", "value": 4},  // (data label scaling multiplier)
    {"name": "_deltaOffsetNEG", "value": 1.1} // (offset adjustment for negative deltas)
  ],

  // ──────────────────────────────
  // TRANSFORMS: derive fields and contextual metadata
  // ──────────────────────────────
  "transform": [
    {
      "window": [
        {"op": "max", "field": "AC", "as": "max_AC"}, // (find highest AC)
        {"op": "min", "field": "AC", "as": "min_AC"}  // (find lowest AC)
      ],
      "frame": [null, null] // (entire dataset scope)
    },
    {"calculate": "now()", "as": "_now"}, // (system time)
    {"calculate": "datetime( now())", "as": "_datetime"}, // (current datetime object)
    {"calculate": "year(utcFormat(datetime( now()), ''%d-%b-%y''))", "as": "_currentYearUtc"}, // (extract current year)
    {"calculate": "utcFormat(datetime( now()), ''%d-%b-%y'')", "as": "_utcFormat"}, // (string format for display)

    // (hardcoded ''today'' date → June 1, 2024)
    {
      "name": "_DATE_TODAY",
      "calculate": "datetime(2024, 5, 1)", // (June 1, 2024, month index = 5)
      "as": "_dateToday"
    },

    {
      // (compare each row''s date to hardcoded ''today'' to classify past/future)
      "name": "_DATE_TEST",
      "calculate": "datum._dateToday > datum[''Date''] ? 0 : 1",
      "as": "_dateTest" // (0 = past/actual, 1 = future/forecast)
    },
    {"calculate": "datum[''AC''] - datum[''AC'']", "as": "__varianceToACan"}, // (placeholder variance calc)
    {"calculate": "datum.__varianceToACan > 0 ? -1: 1", "as": "__deltaOffsetMultiACier"}, // (offset direction)
    {"calculate": "datum.__varianceToACan > 0 ? 1.4 : 1.99", "as": "__deltaOffsetDivider"}, // (offset magnitude)
    {"calculate": "(( datum[''AC''] - datum[''AC''] ) / datum[''AC''])", "as": "__variancePCT"}, // (variance %)
    {"calculate": "(bwDelta-(bwDelta*2))", "as": "_deltaWidth"}, // (delta bar width)
    {"calculate": "(bwACan - bwDelta)", "as": "_offsetACanDelta"}, // (offset for delta placement)
    {"calculate": "datum.__variancePCT > 0 ? -2 : 1", "as": "_deltaOffset"}, // (directional offset)
    {
      "calculate": "datum[''AC''] > datum[''AC''] ? ''#c3f550'' : ''tomato''",
      "as": "_deltaColourFormat" // (placeholder delta color rule)
    },
    {
      "calculate": "datum[''AC''] > datum[''AC''] ? _strokeColour : ''black''",
      "as": "_deltaStrokeFormat" // (stroke color fallback)
    },
    {"calculate": "(bwACan) * -0.169", "as": "_globalOffset"} // (alignment offset)
  ],

  // ──────────────────────────────
  // GLOBAL ENCODING (applied to all layers)
  // ──────────────────────────────
  "encoding": {
    "x": {
      "field": "Date", // (temporal x-axis)
      "type": "nominal", // (treated as categories)
      "axis": {
        "title": null, // (hide title)
        "domain": true, // (draw x baseline)
        "labels": true, // (show tick labels)
        "formatType": "pbiFormat", // (Power BI format)
        "format": "MMM-yy", // (month-year format)
        "labelAngle": 0, // (horizontal labels)
        "labelAlign": "center",
        "labelPadding": 5,
        "labelOffset": 0,
        "labelFontSize": {
          // (responsive label scaling by available bandwidth)
          "expr": " (bandwidth(''x'') / textMultiACier ) * bwActual+3"
        }
      }
    },
    "y": {
      "field": "AC", // (Actual value)
      "type": "quantitative",
      "axis": null, // (hide y-axis)
      "scale": {"zero": false} // (use data-driven range)
    }
  },

  // ──────────────────────────────
  // LAYER DEFINITIONS
  // ──────────────────────────────
  "description": "_LAYER_", // (group reference label)
  "layer": [

    // LAYER 1: BAR MARK
    {
      "description": "ACTUAL_BAR",
      "mark": {
        "type": "bar",
        "xOffset": 0, // (no x-offset)
        "width": {
          "expr": "(bandwidth(''x'')) * (bwACan)" // (width scales with param)
        },
        // (conditional fill: past=actual ivory, future=forecast light)
        "fill": {
          "expr": "datum._dateToday > datum.Date ? _actual_colour : _forecast_colour"
        },
        // (dynamic stroke color: green for max, red for min, else black)
        "stroke": {
          "expr": "datum.AC == datum.max_AC ? ''limegreen'' : datum.AC == datum.min_AC ? ''tomato'' : ''black''"
        },
        // (emphasize extreme bars with thicker border)
        "strokeWidth": {
          "expr": "datum.AC == datum.max_AC || datum.AC == datum.min_AC ? 2 : 0.5"
        }
      }
    },

    // LAYER 2: TEXT LABELS
    {
      "description": "ACTUAL_DATA_LABELS",
      "mark": {
        "type": "text",
        "color": "black", // (static black text)
        "fontSize": {
          "expr": " ( bandwidth(''x'') / labelMultiACier ) * bwActual" // (scaled text size)
        },
        "yOffset": 5, // (vertical nudge down)
        "xOffset": -5, // (horizontal offset)
        "baseline": "top", // (anchor from bar top)
        "align": "left", // (left alignment)
        "fontWeight": "bold" // (bold for clarity)
      },
      "encoding": {
        "text": {
          "field": "AC", // (label content: Actual value)
          "type": "quantitative",
          "format": ".2s" // (SI formatting, e.g., 1.2k)
        }
      }
    }
  ]
}'
