'{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",

  "data": {"name": "dataset"}, // dataset source reference (e.g., Power BI data context)

  // PARAMS (define constants for bar geometry, colors, and scaling)
  "params": [
    {"name": "bwActual", "value": 0.8}, // bar width multiplier for Actual
    {"name": "bwBudget", "value": 0.8}, // bar width multiplier for Budget
    {"name": "bwACan", "value": 0.8}, // bar width multiplier for ACan
    {"name": "bwDelta", "value": 0.15}, // width for variance/Delta overlays

    {"name": "_strokeColour", "value": "black"}, // outline stroke color
    {"name": "_actualColour", "value": "#404040"}, // dark color for Actuals
    {"name": "_forecastColour", "value": "#f0eee9"}, // light gray for forecast fill
    {"name": "_forecast_colour", "value": "url(#diagonal-stripe-1-30)"}, // forecast pattern fill
    {"name": "_actual_colour", "value": "ivory"}, // ivory for Actual bars

    {"name": "textMultiACier", "value": 4.5}, // scaling factor for axis labels
    {"name": "labelMultiACier", "value": 4}, // scaling factor for in-bar labels

    {"name": "_deltaOffsetNEG", "value": 1.1} // offset control for Delta positioning
  ],

  // TRANSFORM (derive helper and variance fields)
  "transform": [
    {
      // compute global max/min of AC (contextual for range or color mapping)
      "window": [
        {"op": "max", "field": "AC", "as": "max_AC"},
        {"op": "min", "field": "AC", "as": "min_AC"}
      ],
      "frame": [null, null]
    },
    {"calculate": "now()", "as": "_now"}, // current timestamp reference
    {"calculate": "datetime( now())", "as": "_datetime"}, // datetime object
    {
      "calculate": "year(utcFormat(datetime( now()), ''%d-%b-%y''))",
      "as": "_currentYearUtc" // extract UTC year for context
    },
    {
      "calculate": "utcFormat(datetime( now()), ''%d-%b-%y'')",
      "as": "_utcFormat" // UTC formatted date string
    },
    {
      // hardcoded “today” date — used for past/future comparisons
      "name": "_DATE_TODAY",
      "calculate": "datetime(2024, 5, 1)", // June 1, 2024 (month index 5)
      "as": "_dateToday"
    },
    {
      // binary test comparing data Date to _dateToday
      "name": "_DATE_TEST",
      "calculate": "datum._dateToday > datum[''Date''] ? 0 : 1 ",
      "as": "_dateTest" // 0 = past (Actual), 1 = future (Forecast)
    },
    {
      "calculate": "datum[''AC''] - datum[''AC'']", // placeholder variance calc (resolves to 0)
      "as": "__varianceToACan"
    },
    {
      // offset multiplier based on sign (unused but kept for consistency)
      "calculate": "datum.__varianceToACan > 0 ? -1: 1",
      "as": "__deltaOffsetMultiACier"
    },
    {
      // adjust divider depending on sign
      "calculate": "datum.__varianceToACan > 0 ? 1.4 : 1.99",
      "as": "__deltaOffsetDivider"
    },
    {
      // placeholder variance percentage
      "calculate": "(( datum[''AC''] - datum[''AC''] ) / datum[''AC''])",
      "as": "__variancePCT"
    },
    {
      // derived width for delta overlays
      "calculate": "(bwDelta-(bwDelta*2))",
      "as": "_deltaWidth"
    },
    {
      // ⚠ preserved typo ("calcaulte") from original
      "calcaulte": "(bwACan - bwDelta)",
      "as": "_offsetACanDelta"
    },
    {
      // ⚠ preserved typo ("calcuate") from original
      "calcuate": "datum.__variancePCT > 0 ? -2 : 1",
      "as": "_deltaOffset"
    },
    {
      // placeholder color calc (always resolves to false branch)
      "calculate": "datum[''AC''] > datum[''AC''] ? ''#c3f550'' : ''tomato''",
      "as": "_deltaColourFormat"
    },
    {
      // placeholder stroke color (also resolves to false branch)
      "calculate": "datum[''AC''] > datum[''AC''] ? _strokeColour : ''black''",
      "as": "_deltaStrokeFormat"
    },
    {
      // small offset to visually align multi-layer bars
      "calculate": "(bwACan) * -0.169",
      "as": "_globalOffset"
    }
  ],

  // ENCODING (shared across all layers)
  "encoding": {
    "x": {
      "field": "Date", // x-axis uses categorical date (MMM-YY)
      "type": "nominal",
      "axis": {
        "title": null, // hide x-axis title
        "domain": true, // draw axis baseline
        "labels": true, // display tick labels
        "formatType": "pbiFormat", // Power BI-friendly formatting
        "format": "MMM-yy", // e.g., Jan-24
        "labelAngle": 0, // horizontal text
        "labelAlign": "center",
        "labelPadding": 5,
        "labelOffset": 0,
        // dynamically scale label font with available bandwidth
        "labelFontSize": {
          "expr": " (bandwidth(''x'') / textMultiACier ) * bwActual+3"
        }
      }
    },
    "y": {
      "field": "AC", // measure plotted on y-axis
      "type": "quantitative",
      "axis": null, // suppress axis display
      "scale": {"zero": false} // flexible y-scale (not forced to zero)
    }
  },

  "description": "_LAYER_", // label for documentation

  // LAYERS (draw order: bar → label)
  "layer": [
    {
      "description": "ACTUAL_BAR", // bar mark for Actual + Forecast
      "mark": {
        "type": "bar", // rectangular mark
        "xOffset": 0, // no horizontal offset
        // bar width scales with parameterized factor
        "width": {
          "expr": "(bandwidth(''x'')) * (bwACan )"
        },
        // conditional fill based on date comparison
        "fill": {
          // if Date < _dateToday → Actual (ivory), else Forecast (pattern)
          "expr": "datum._dateToday > datum.Date ? _actual_colour : _forecast_colour"
        },
        "stroke": "grey" // border to separate bars visually
      }
    },
    {
      "description": "ACTUAL_DATA_LABELS", // label mark for numeric values
      "mark": {
        "type": "text", // renders text at each bar
        "color": "black", // static label color
        // text size dynamically scaled to bandwidth
        "fontSize": {
          "expr": " ( bandwidth(''x'')/ labelMultiACier ) * bwActual"
        },
        "yOffset": 5, // vertical adjustment downwards
        "xOffset": -5, // small left shift
        "baseline": "top", // anchored to bar top
        "align": "left", // left-align text
        "fontWeight": "bold" // bold for visibility
      },
      "encoding": {
        "text": {
          "field": "AC", // display AC value on each bar
          "type": "quantitative",
          "format": ".2s" // compact number formatting (e.g., 1.2k)
        }
      }
    }
  ]
}'
