'{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",

  "data": {"name": "dataset"}, // external dataset (bound via Power BI or Vega data binding)

  // PARAMETERS (define constants for widths, colors, text, and formatting)
  "params": [
    {"name": "bwActual", "value": 0.8}, // Actual bar width multiplier
    {"name": "bwBudget", "value": 0.8}, // Budget bar width multiplier
    {"name": "bwPlan", "value": 0.8}, // Plan bar width multiplier
    {"name": "bwDelta", "value": 0.15}, // Delta (variance) bar width multiplier

    {"name": "_strokeColour", "value": "black"}, // outline color for bars
    {"name": "_actualColour", "value": "navy"}, // fill color for Actuals
    {"name": "_forecastColour", "value": "#f0eee9"}, // forecast fill color
    {"name": "_forecastPlanColour", "value": "url(#diagonal-stripe-1-30)"}, // pattern fill for forecast plan
    {"name": "_planColour", "value": "white"}, // plan bar fill color

    {"name": "textMultiplier", "value": 4.5}, // text scaling for axis labels
    {"name": "labelMultiplier", "value": 4}, // text scaling for in-bar labels

    {"name": "_deltaOffsetNEG", "value": 1.1} // offset for negative delta adjustments
  ],

  // TRANSFORMS (compute date-based logic, variances, and formatting helpers)
  "transform": [
    {
      // compute global max/min of AC for scale context
      "window": [
        {"op": "max", "field": "AC", "as": "max_AC"},
        {"op": "min", "field": "AC", "as": "min_AC"}
      ],
      "frame": [null, null]
    },
    {
      "calculate": "now()", "as": "_now" // current timestamp
    },
    {
      "calculate": "datetime( now())", "as": "_datetime" // datetime object for reuse
    },
    {
      // current UTC year (numeric)
      "calculate": "year(utcFormat(datetime( now()), ''%d-%b-%y''))",
      "as": "_currentYearUtc"
    },
    {
      // formatted UTC date string
      "calculate": "utcFormat(datetime( now()), ''%d-%b-%y'')",
      "as": "_utcFormat"
    },
    {
      "name": "_DATE_TODAY",
      "calculate": "datetime(2024, 5, 1)", 
      "as": "_dateToday"
    },
    {
      // compares today''s date to record Date — used to color labels differently
      "name": "_DATE_TEST",
      "calculate": "datum._dateToday > datum[''Date''] ? 0 : 1 ",
      "as": "_dateTest" // 0 = past, 1 = future
    },
    {
      // compute absolute variance between Actual and Plan
      "calculate": "datum[''AC''] - datum[''PL'']",
      "as": "__varianceToPlan"
    },
    {
      // variance sign determines label offset direction
      "calculate": "datum.__varianceToPlan > 0 ? -1: 1",
      "as": "__deltaOffsetMultiplier"
    },
    {
      // adjusts offset scaling by variance sign
      "calculate": "datum.__varianceToPlan > 0 ? 1.4 : 1.99",
      "as": "__deltaOffsetDivider"
    },
    {
      // computes % variance relative to Plan
      "calculate": "(( datum[''AC''] - datum[''PL''] ) / datum[''PL''])",
      "as": "__variancePCT"
    },
    {
      // computes a symmetrical negative width for delta overlay centering
      "calculate": "(bwDelta-(bwDelta*2))",
      "as": "_deltaWidth"
    },
    {
      // ⚠ Typo preserved: “calcaulte” (would be ignored at runtime)
      // intended to calculate offset between Plan and Delta
      "calcaulte": "(bwPlan - bwDelta)",
      "as": "_offsetPlanDelta"
    },
    {
      // ⚠ Typo preserved: “calcuate” (non-functional)
      // intended to offset delta mark by variance direction
      "calcuate": "datum.__variancePCT > 0 ? -2 : 1",
      "as": "_deltaOffset"
    },
    {
      // color formatting for delta bars (green if Actual > Plan, else red)
      "calculate": "datum[''AC''] > datum[''PL''] ? ''#c3f550'' : ''tomato''",
      "as": "_deltaColourFormat"
    },
    {
      // stroke color formatting based on variance sign
      "calculate": "datum[''AC''] > datum[''PL''] ? _strokeColour : ''black''",
      "as": "_deltaStrokeFormat"
    },
    {
      // small offset for horizontal alignment correction
      "calculate": "(bwPlan) * -0.169",
      "as": "_globalOffset"
    }
  ],

  // GLOBAL ENCODING (applies to all layers unless overridden)
  "encoding": {
    "x": {
      "field": "Date", // nominal x-axis (month/year)
      "type": "nominal",
      "axis": {
        "title": null, // hides axis title
        "domain": true, // keeps baseline visible
        "labels": true, // shows tick labels
        "formatType": "pbiFormat", // Power BI formatting
        "format": "MMM-yy", // short month-year format
        "labelAngle": 0, // horizontal text
        "labelAlign": "center",
        "labelPadding": 5,
        "labelOffset": 0,
        // dynamic font size scaling based on bandwidth and multiplier
        "labelFontSize": {
          "expr": " (bandwidth(''x'') / textMultiplier ) * bwActual+3"
        }
      }
    },
    "y": {
      "field": "AC", // quantitative field for bar height
      "type": "quantitative",
      "axis": null, // hides y-axis lines
      "scale": {"zero": false} // auto-range scale (not anchored at 0)
    },
    "color": {"value": "navy"} // default fill color for Actual bars
  },

  "description": "_LAYER_", // descriptive metadata for the layer group

  // LAYERS (stacked drawing order)
  "layer": [
    {
      "description": "ACTUAL_BAR", // main Actual value bar layer
      "mark": {
        "type": "bar", // rectangular mark for bar chart
        // dynamically computed width based on scale bandwidth and parameter
        "width": {
          "expr": "(bandwidth(''x'')) * (bwActual )"
        },
        // stroke applied from param (black outline)
        "stroke": {
          "expr": "_strokeColour"
        },
        "strokeWidth": 0.6 // thin outline
      }
    },
    {
      "description": "ACTUAL_DATA_LABELS", // second layer for labels
      "mark": {
        "type": "text", // text mark for numeric labels
        // conditional label color: white for past, black for future
        "color": {
          "expr": "datum._dateTest == 0 ? ''white'' : ''black''"
        },
        // dynamically scale font size by bar width and label multiplier
        "fontSize": {
          "expr": " ( bandwidth(''x'')/ labelMultiplier ) * bwActual"
        },
        "yOffset": 3, // slight vertical nudge down
        "xOffset": -4, // horizontal nudge left
        "baseline": "top", // aligns text above bar top
        "align": "left", // aligns text to left of position anchor
        "fontWeight": "bold" // makes labels stand out
      },
      "encoding": {
        // bind text to AC field, formatted as short decimal (e.g. 1.2k)
        "text": {
          "field": "AC",
          "type": "quantitative",
          "format": ".2s" // SI notation for compact readability
        },
        "color": {"value": "white"} // fallback color (redundant to mark expr)
      }
    }
  ]
}'
