'{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",

  "data": {"name": "dataset"}, // external dataset (e.g., Power BI dataset binding)

  // PARAMETERS (constants and reusable settings for width, color, etc.)
  "params": [
    {"name": "bwActual", "value": 0.8}, // width multiplier for Actual bars
    {"name": "bwBudget", "value": 0.8}, // width multiplier for Budget bars
    {"name": "bwACan", "value": 0.8}, // width multiplier for ACan (Actual Candidate) bars
    {"name": "bwDelta", "value": 0.15}, // width multiplier for Delta overlay bars

    // color and pattern parameters
    {"name": "_strokeColour", "value": "black"}, // stroke (outline) color
    {"name": "_actualColour", "value": "#404040"}, // Actual bar color
    {"name": "_forecastColour", "value": "#f0eee9"}, // Forecast bar color
    {"name": "_forecastACanColour", "value": "url(#diagonal-stripe-1-30)"}, // pattern fill for forecast ACan bars
    {"name": "_ACanColour", "value": "ivory"}, // ACan bar color

    // text sizing multipliers
    {"name": "textMultiACier", "value": 4.5}, // affects x-axis label font scaling
    {"name": "labelMultiACier", "value": 4}, // affects label spacing

    // offset control for negative deltas
    {"name": "_deltaOffsetNEG", "value": 1.1}
  ],

  // TRANSFORM SECTION (prepare derived fields and helper calculations)
  "transform": [
    {
      // compute global min/max for AC (Actual) values
      "window": [
        {"op": "max", "field": "AC", "as": "max_AC"},
        {"op": "min", "field": "AC", "as": "min_AC"}
      ],
      "frame": [null, null]
    },
    {
      "calculate": "now()", "as": "_now" // captures current timestamp
    },
    {
      "calculate": "datetime( now())", "as": "_datetime" // stores JS datetime object
    },
    {
      // extracts current year (UTC)
      "calculate": "year(utcFormat(datetime( now()), ''%d-%b-%y''))",
      "as": "_currentYearUtc"
    },
    {
      // formatted UTC date string
      "calculate": "utcFormat(datetime( now()), ''%d-%b-%y'')",
      "as": "_utcFormat"
    },
    {
      // captures today’s date
      "name": "_DATE_TODAY",
      "calculate": "datetime( now()) ",
      "as": "_dateToday"
    },
    {
      // binary test comparing today’s date vs dataset’s Date field
      "name": "_DATE_TEST",
      "calculate": "datum._dateToday > datum[''Date''] ? 0 : 1 ",
      "as": "_dateTest" // 0 = past, 1 = future
    },
    {
      // variance placeholder (currently AC - AC = 0 for demonstration)
      "calculate": "datum[''AC''] - datum[''AC'']",
      "as": "__varianceToACan"
    },
    {
      // determines direction multiplier (based on variance)
      "calculate": "datum.__varianceToACan > 0 ? -1: 1",
      "as": "__deltaOffsetMultiACier"
    },
    {
      // divider adjustment for delta offset scaling
      "calculate": "datum.__varianceToACan > 0 ? 1.4 : 1.99",
      "as": "__deltaOffsetDivider"
    },
    {
      // computes percentage variance (zero since AC - AC = 0)
      "calculate": "(( datum[''AC''] - datum[''AC''] ) / datum[''AC''])",
      "as": "__variancePCT"
    },
    {
      // computes negative delta width (centering factor)
      "calculate": "(bwDelta-(bwDelta*2))",
      "as": "_deltaWidth"
    },
    {
      // ⚠ Typo preserved intentionally: “calcaulte” (no effect in Vega-Lite, would need correction)
      // supposed to compute offset between ACan and Delta
      "calcaulte": "(bwACan - bwDelta)",
      "as": "_offsetACanDelta"
    },
    {
      // ⚠ Typo preserved intentionally: “calcuate” instead of “calculate”
      // intended to set offset depending on variance direction
      "calcuate": "datum.__variancePCT > 0 ? -2 : 1",
      "as": "_deltaOffset"
    },
    {
      // conditional color: green for positive variance, red for negative
      "calculate": "datum[''AC''] > datum[''AC''] ? ''#c3f550'' : ''tomato''",
      "as": "_deltaColourFormat"
    },
    {
      // conditional stroke color (black fallback)
      "calculate": "datum[''AC''] > datum[''AC''] ? _strokeColour : ''black''",
      "as": "_deltaStrokeFormat"
    },
    {
      // global offset correction for horizontal positioning
      "calculate": "(bwACan) * -0.169",
      "as": "_globalOffset"
    }
  ],

  // ENCODING (defines shared axes and scales)
  "encoding": {
    "x": {
      "field": "Date", // x-axis categorical date field
      "type": "nominal",
      "axis": {
        "title": null, // removes title
        "domain": true, // keeps baseline visible
        "labels": true, // show date labels
        "formatType": "pbiFormat", // Power BI-friendly date formatting
        "format": "MMM-yy", // short month-year label
        "labelAngle": 0, // horizontal labels
        "labelAlign": "center",
        "labelPadding": 5,
        "labelOffset": 0,
        // dynamic font size based on bar width and multiplier
        "labelFontSize": {
          "expr": " (bandwidth(''x'') / textMultiACier ) * bwActual+3"
        }
      }
    },
    "y": {
      "field": "AC", // y-axis measure (Actual)
      "type": "quantitative",
      "axis": null, // hides axis lines and ticks
      "scale": {"zero": false} // scale adjusts to data min/max
    }
  },

  "description": "_LAYER_", // layer metadata tag

  // LAYERS (marks drawn in rendering order)
  "layer": [
    {
      "description": "ACTUAL_BAR", // primary bar layer for Actuals
      "mark": {
        "type": "bar", // rectangular bar mark
        "xOffset": 0, // aligns directly with x position
        // width based on parameterized multiplier and scale bandwidth
        "width": {
          "expr": "(bandwidth(''x'')) * (bwACan )"
        }
      }
    }
  ]
}'
