Literal.Value:
'{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",

  "data": {"name": "dataset"}, // dataset reference (e.g., from Power BI or external binding)

  // PARAMETERS (define configurable constants for widths, colors, and scaling)
  "params": [
    {"name": "bwActual", "value": 0.8}, // width multiplier for Actual bars
    {"name": "bwBudget", "value": 0.8}, // width multiplier for Budget bars
    {"name": "bwPlan", "value": 0.8}, // width multiplier for Plan bars
    {"name": "bwDelta", "value": 0.15}, // width multiplier for Delta overlays

    {"name": "_strokeColour", "value": "black"}, // stroke color for outlines
    {"name": "_actualColour", "value": "navy"}, // base fill color for Actuals
    {"name": "_forecastColour", "value": "#f0eee9"}, // color for forecast bars
    {"name": "_forecastPlanColour", "value": "url(#diagonal-stripe-1-30)"}, // patterned fill for forecast plan
    {"name": "_planColour", "value": "white"}, // plan bar color

    {"name": "textMultiplier", "value": 4.5}, // controls axis label font scaling
    {"name": "labelMultiplier", "value": 4}, // controls data label font scaling

    {"name": "_deltaOffsetNEG", "value": 1.1} // used for vertical offset in delta visuals
  ],

  // TRANSFORM (prepare helper fields and computed logic)
  "transform": [
    {
      // get global max and min of AC (for contextual scale reference)
      "window": [
        {"op": "max", "field": "AC", "as": "max_AC"},
        {"op": "min", "field": "AC", "as": "min_AC"}
      ],
      "frame": [null, null]
    },
    {
      "calculate": "now()", "as": "_now" // current timestamp reference
    },
    {
      "calculate": "datetime( now())", "as": "_datetime" // explicit datetime object
    },
    {
      // extract current UTC year for potential temporal logic
      "calculate": "year(utcFormat(datetime( now()), ''%d-%b-%y''))",
      "as": "_currentYearUtc"
    },
    {
      // formatted UTC string (day-month-year)
      "calculate": "utcFormat(datetime( now()), ''%d-%b-%y'')",
      "as": "_utcFormat"
    },
    {
      // hardcoded “today” reference: June 1st, 2024
      "name": "_DATE_TODAY",
      "calculate": "datetime(2024, 5, 1)", // month index 5 = June
      "as": "_dateToday"
    },
    {
      // binary field comparing dataset date to hardcoded “today”
      "name": "_DATE_TEST",
      "calculate": "datum._dateToday > datum[''Date''] ? 0 : 1 ",
      "as": "_dateTest" // 0 = past, 1 = future
    },
    {
      // difference between Actual (AC) and Plan (PL)
      "calculate": "datum[''AC''] - datum[''PL'']",
      "as": "__varianceToPlan"
    },
    {
      // sign-based offset multiplier
      "calculate": "datum.__varianceToPlan > 0 ? -1: 1",
      "as": "__deltaOffsetMultiplier"
    },
    {
      // scaling divider for offset adjustments
      "calculate": "datum.__varianceToPlan > 0 ? 1.4 : 1.99",
      "as": "__deltaOffsetDivider"
    },
    {
      // compute relative % variance
      "calculate": "(( datum[''AC''] - datum[''PL''] ) / datum[''PL''])",
      "as": "__variancePCT"
    },
    {
      // delta width adjustment (negative for centering)
      "calculate": "(bwDelta-(bwDelta*2))",
      "as": "_deltaWidth"
    },
    {
      // horizontal offset between Plan and Delta
      "calculate": "(bwPlan - bwDelta)",
      "as": "_offsetPlanDelta"
    },
    {
      // offset direction depending on variance sign
      "calculate": "datum.__variancePCT > 0 ? -2 : 1",
      "as": "_deltaOffset"
    },
    {
      // color-coding deltas: green (positive) vs red (negative)
      "calculate": "datum[''AC''] > datum[''PL''] ? ''#c3f550'' : ''tomato''",
      "as": "_deltaColourFormat"
    },
    {
      // stroke color for delta markers (black fallback)
      "calculate": "datum[''AC''] > datum[''PL''] ? _strokeColour : ''black''",
      "as": "_deltaStrokeFormat"
    },
    {
      // small adjustment for horizontal alignment across layers
      "calculate": "(bwPlan) * -0.169",
      "as": "_globalOffset"
    }
  ],

  // GLOBAL ENCODING (applies to all layered marks)
  "encoding": {
    "x": {
      "field": "Date", // date or month-year on x-axis
      "type": "nominal",
      "axis": {
        "title": null, // remove title
        "domain": true, // keep baseline visible
        "labels": true, // show tick labels
        "formatType": "pbiFormat", // Power BI formatting syntax
        "format": "MMM-yy", // abbreviated month-year
        "labelAngle": 0, // horizontal alignment
        "labelAlign": "center",
        "labelPadding": 5,
        "labelOffset": 0,
        // dynamic scaling for label font size (based on bar width)
        "labelFontSize": {
          "expr": " (bandwidth(''x'') / textMultiplier ) * bwActual+3"
        }
      }
    },
    "y": {
      "field": "AC", // Actual values as quantitative measure
      "type": "quantitative",
      "axis": null, // hides y-axis for clean visual
      "scale": {"zero": false} // prevents forced zero baseline
    },
    "color": {
      // conditional fill: future dates (1) = whitesmoke, else navy
      "condition": [
        {
          "test": "datum._dateTest == 1",
          "value": "whitesmoke"
        }
      ],
      "value": "navy" // default color for past Actuals
    }
  },

  "description": "_LAYER_", // identifies the layer grouping (for documentation or reuse)

  // LAYERS (marks rendered in order)
  "layer": [
    {
      "description": "ACTUAL_BAR", // base bar layer
      "mark": {
        "type": "bar", // rectangular bar
        // width dynamically scaled via parameters
        "width": {
          "expr": "(bandwidth(''x'')) * (bwActual )"
        },
        // stroke border from parameterized color
        "stroke": {
          "expr": "_strokeColour"
        },
        "strokeWidth": 0.6 // fine outline for separation
      }
    },
    {
      "description": "ACTUAL_BAR", // secondary bar (overlay or highlight)
      "mark": {
        "type": "bar",
        "width": {
          "expr": "(bandwidth(''x'')) * (bwActual )"
        },
        "strokeWidth": 0.6
      }
    },
    {
      "description": "ACTUAL_DATA_LABELS", // text label layer
      "mark": {
        "type": "text", // numeric labels on bars
        // conditional label color (white for past, black for future)
        "color": {
          "expr": "datum._dateTest == 0 ? ''white'' : ''black''"
        },
        // dynamically scale font size relative to bar width
        "fontSize": {
          "expr": " ( bandwidth(''x'')/ labelMultiplier ) * bwActual"
        },
        "yOffset": 3, // slight vertical nudge
        "xOffset": -4, // left horizontal offset
        "baseline": "top", // anchored to top of bar
        "align": "left", // left-aligned text
        "fontWeight": "bold" // bold for visibility
      },
      "encoding": {
        // bind label text to AC values
        "text": {
          "field": "AC",
          "type": "quantitative",
          "format": ".2s" // SI-style compact format (e.g., 1.2k)
        },
        "color": {
          // same conditional color logic replicated for encoding consistency
          "condition": [
            {
              "test": "datum._dateTest == 1",
              "value": "black"
            }
          ],
          "value": "white"
        }
      }
    }
  ]
}'
