Literal.Value:
'{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "data": {
    "values": [
      {
        "label": "Begin",
        "amount": 4000
      },
      {"label": "Jan", "amount": 1707},
      {"label": "Feb", "amount": -1425},
      {"label": "Mar", "amount": -1030},
      {"label": "Apr", "amount": 1812},
      {"label": "May", "amount": -1067},
      {"label": "Jun", "amount": -1481},
      {"label": "Jul", "amount": 1228},
      {"label": "Aug", "amount": 1176},
      {"label": "Sep", "amount": 1146},
      {"label": "Oct", "amount": 1205},
      {"label": "Nov", "amount": -1388},
      {"label": "Dec", "amount": 1492},
      {"label": "End", "amount": 0}
    ]
  },
  "params": [
    {
      "name": "bwRule", "value": 2
    }],
  "transform": [
    {
      "window": [
        {
          "op": "sum",
          "field": "amount",
          "as": "sum"
        }
      ]
    },
    {
      "window": [
        {
          "op": "lead",
          "field": "label",
          "as": "lead"
        }
      ]
    },
    {
      "calculate": "datum.lead === null ? datum.label : datum.lead",
      "as": "lead"
    },
    {
      "calculate": "datum.label === ''End'' ? 0 : datum.sum - datum.amount",
      "as": "previous_sum"
    },
    {
      "calculate": "datum.label === ''End'' ? datum.sum : datum.amount",
      "as": "amount"
    },
    {
      "calculate": "(datum.label !== ''Begin'' && datum.label !== ''End'' && datum.amount > 0 ? ''+'' : '''') + datum.amount",
      "as": "text_amount"
    },
    {
      "calculate": "(datum.sum + datum.previous_sum) / 2",
      "as": "center"
    },
    {
      "calculate": "datum.sum < datum.previous_sum ? datum.sum : ''''",
      "as": "sum_dec"
    },
    {
      "calculate": "datum.sum > datum.previous_sum ? datum.sum : ''''",
      "as": "sum_inc"
    }
  ],
  "encoding": {
    "x": {
      "field": "label",
      "type": "ordinal",
      "sort": null,
      "axis": {
        "labelAngle": 0,
        "title": "Months"
      }
    }
  },
  "layer": [
    {
      "mark": {"type": "bar"},
      "encoding": {
        "y": {
          "field": "previous_sum",
          "type": "quantitative",
          "title": "Amount",
          "scale": {
            "domain": [0,8000]
          }
        },
        "y2": {"field": "sum"},
        "color": {
          "condition": [
            {
              "test": "datum.label === ''Begin'' || datum.label === ''End''",
              "value": "#f7e0b6"
            },
            {
              "test": "datum.sum < datum.previous_sum",
              "value": "#f78a64"
            }
          ],
          "value": "#93c4aa"
        }
      }
    },
    {
      "mark": {
        "type": "rule",
        "color": "#404040",
        "opacity": 1,
        "strokeWidth": 2,
        "xOffset": {"expr": "(bandwidth(''x'')) / bwRule *-1"},
        "x2Offset": {"expr": "(bandwidth(''x'')) / bwRule"}
      },
      "encoding": {
        "x2": {"field": "lead"},
        "y": {
          "field": "sum",
          "type": "quantitative"
        }
      }
    },
    {
      "mark": {
        "type": "text",
        "size": 10,
        "color": {
          "expr": "datum.sum_inc == 0 ? ''transparent'' : ''black''"
        },
        "dy": -4,
        "baseline": "bottom"
      },
      "encoding": {
        "y": {
          "field": "sum_inc",
          "type": "quantitative"
        },
        "text": {
          "field": "sum_inc",
          "type": "nominal",
          "format": ",.2~s"
        }
      }
    },
    {
      "mark": {
        "type": "text",
        "size": 10,
        "dy": 4,
        "baseline": "top",
        "color": {
          "expr": "datum.sum_dec == 0 ? ''transparent'' : ''black''"
        }
      },
      "encoding": {
        "y": {
          "field": "sum_dec",
          "type": "quantitative"
        },
        "text": {
          "field": "sum_dec",
          "type": "nominal",
          "format": ",.2~s"
        }
      }
    },
    {
      "mark": {
        "type": "text",
        "size": 8,
        "fontWeight": "bold",
        "baseline": "middle"
      },
      "encoding": {
        "y": {
          "field": "center",
          "type": "quantitative"
        },
        "text": {
          "field": "text_amount",
          "type": "nominal",
          "format": ",.2~s"
        },
        "color": {
          "condition": [
            {
              "test": "datum.label === ''Begin'' || datum.label === ''End''",
              "value": "#725a30"
            }
          ],
          "value": "white"
        }
      }
    }
  ],
  "config": {
    "text": {
      "fontWeight": "bold",
      "color": "#404040"
    }
  }
}'
