'{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "description": "Waterfall chart showing running totals of monthly amounts.",

  "data": {
    "values": [
      {"label": "Begin", "amount": 4000},
      {"label": "Jan", "amount": 1707},
      {"label": "Feb", "amount": -1425},
      {"label": "Mar", "amount": -1030},
      {"label": "Apr", "amount": 1812},
      {"label": "May", "amount": -1067},
      {"label": "Jun", "amount": -1481},
      {"label": "Jul", "amount": 1228},
      {"label": "Aug", "amount": 1176},
      {"label": "Sep", "amount": 1146},
      {"label": "Oct", "amount": 1205},
      {"label": "Nov", "amount": -1388},
      {"label": "Dec", "amount": 1492},
      {"label": "End", "amount": 0}
    ]
  },

  // PARAMETER (allows dynamic adjustment, e.g., for bar width offset)
  "params": [
    {"name": "bwRule", "value": 2} // used later in mark xOffset expressions
  ],

  // TRANSFORM SECTION (data preparation and calculations)
  "transform": [
    {
      // running cumulative sum of ''amount'' (gives each step total)
      "window": [{"op": "sum", "field": "amount", "as": "sum"}]
    },
    {
      // add the next label for each row (for drawing connecting rules)
      "window": [{"op": "lead", "field": "label", "as": "lead"}]
    },
    {
      // ensure the final ''lead'' field is not null at end
      "calculate": "datum.lead === null ? datum.label : datum.lead",
      "as": "lead"
    },
    {
      // calculate previous cumulative sum (to form bar base)
      "calculate": "datum.label === ''End'' ? 0 : datum.sum - datum.amount",
      "as": "previous_sum"
    },
    {
      // normalize ''amount'' so ''End'' uses final total instead of zero
      "calculate": "datum.label === ''End'' ? datum.sum : datum.amount",
      "as": "amount"
    },
    {
      // label text with sign (+/-) prefix where applicable
      "calculate": "(datum.label !== ''Begin'' && datum.label !== ''End'' && datum.amount > 0 ? ''+'' : '''') + datum.amount",
      "as": "text_amount"
    },
    {
      // find midpoint between cumulative values (for label placement)
      "calculate": "(datum.sum + datum.previous_sum) / 2",
      "as": "center"
    },
    {
      // identify decreases (for downward labels)
      "calculate": "datum.sum < datum.previous_sum ? datum.sum : ''''",
      "as": "sum_dec"
    },
    {
      // identify increases (for upward labels)
      "calculate": "datum.sum > datum.previous_sum ? datum.sum : ''''",
      "as": "sum_inc"
    }
  ],

  // GLOBAL ENCODING (shared x position)
  "encoding": {
    "x": {
      "field": "label",
      "type": "ordinal", // discrete months
      "sort": null, // preserve input order
      "axis": {
        "labelAngle": 0, // keep month labels horizontal
        "title": "Months" // x-axis title
      }
    }
  },

  // LAYERED VISUALIZATION
  "layer": [
    {
      // LAYER 1: main bars (each step in running total)
      "mark": {"type": "bar"},
      "encoding": {
        "y": {
          "field": "previous_sum", // bar start position (lower value)
          "type": "quantitative",
          "title": "Amount", // shared y-axis title
          "scale": {
            "domain": [0, 8000] // fixed y-scale range for consistent comparison
          }
        },
        "y2": {"field": "sum"}, // bar end position (higher value)

        // COLOR LOGIC (conditional fill)
        "color": {
          "condition": [
            {
              "test": "datum.label === ''Begin'' || datum.label === ''End''", // total bars
              "value": "#f7e0b6" // beige color for totals
            },
            {
              "test": "datum.sum < datum.previous_sum", // decreasing bars
              "value": "#f78a64" // orange-red for decrease
            }
          ],
          "value": "#93c4aa" // default green for increase
        }
      }
    },
    {
      // LAYER 2: connector rules between bars
      "mark": {
        "type": "rule", // horizontal connector lines
        "color": "#404040", // dark gray
        "opacity": 1,
        "strokeWidth": 2,
        // xOffset controls connector width around bar centers
        "xOffset": {"expr": "(bandwidth(''x'')) / bwRule * -1"},
        "x2Offset": {"expr": "(bandwidth(''x'')) / bwRule"}
      },
      "encoding": {
        "x2": {"field": "lead"}, // connects current bar to next
        "y": {
          "field": "sum", // connect at cumulative total level
          "type": "quantitative"
        }
      }
    },
    {
      // LAYER 3: text labels for upward (increasing) totals
      "mark": {
        "type": "text",
        "size": 10,
        "color": {
          // hide text if not applicable
          "expr": "datum.sum_inc == 0 ? ''transparent'' : ''black''"
        },
        "dy": -4, // vertical offset above bar
        "baseline": "bottom" // position text just above value
      },
      "encoding": {
        "y": {
          "field": "sum_inc",
          "type": "quantitative"
        },
        "text": {
          "field": "sum_inc",
          "type": "nominal",
          "format": ",.2~s" // abbreviated format (e.g., 1.2k)
        }
      }
    },
    {
      // LAYER 4: text labels for downward (decreasing) totals
      "mark": {
        "type": "text",
        "size": 10,
        "dy": 4, // vertical offset below bar
        "baseline": "top", // position text beneath the value
        "color": {
          "expr": "datum.sum_dec == 0 ? ''transparent'' : ''black''"
        }
      },
      "encoding": {
        "y": {
          "field": "sum_dec",
          "type": "quantitative"
        },
        "text": {
          "field": "sum_dec",
          "type": "nominal",
          "format": ",.2~s"
        }
      }
    },
    {
      // LAYER 5: internal text labels (display change amounts inside bars)
      "mark": {
        "type": "text",
        "size": 8,
        "fontWeight": "bold",
        "baseline": "middle" // vertically centered within bar
      },
      "encoding": {
        "y": {
          "field": "center", // label position midway along bar height
          "type": "quantitative"
        },
        "text": {
          "field": "text_amount", // formatted +/âˆ’ value
          "type": "nominal",
          "format": ",.2~s"
        },
        // COLOR LOGIC (conditional)
        "color": {
          "condition": [
            {
              "test": "datum.label === ''Begin'' || datum.label === ''End''",
              "value": "#725a30" // dark brown for total labels
            }
          ],
          "value": "white" // white text for middle bars
        }
      }
    }
  ],

  // CONFIG SECTION (styling defaults)
  "config": {
    "text": {
      "fontWeight": "bold",
      "color": "#404040" // default dark gray for text marks
    }
  }
}'
