Literal.Value:
'{
  "data": {"name": "dataset"}, // Data source (fields: Date, Value, KPI, MaxDate, Actual, Format Variance, etc.)

  "layer": [

    // ───────────────────────────────
    // Layer 1: Baseline reference rules
    // Draws vertical or horizontal reference rules across data points.
    // Used for subtle background structure.
    // ───────────────────────────────
    {
      "mark": {"type": "rule"}, // Simple reference line for each data record.
      "encoding": {
        "color": {"value": "lightgray"}, // Light gray line color for minimal visual weight.
        "x": {
          "field": "Date",               // Temporal X-axis (time dimension).
          "type": "temporal",
          "axis": null                   // Axis hidden for clean presentation.
        },
        "y": {
          "field": "Value",              // Quantitative Y-axis (numeric metric).
          "type": "quantitative",
          "axis": null                   // No visible axis.
        }
      }
    },

    // ───────────────────────────────
    // Layer 2: Data points (gray circles)
    // Plots main time-series values as circles, providing a trend of data points.
    // ───────────────────────────────
    {
      "mark": {
        "type": "circle",
        "point": {
          "filled": false,  // Hollow point markers for neutral data indication.
          "fill": "white"   // White interior for contrast.
        },
        "tooltip": true,    // Enables hover tooltips for detailed values.
        "size": 50          // Marker size for visibility.
      },
      "encoding": {
        "color": {"value": "gray"}, // Default neutral point color.
        "x": {
          "field": "Date",          // Time field for positioning.
          "type": "temporal"
        },
        "y": {
          "field": "Value",         // Value determines vertical position.
          "type": "quantitative"
        }
      }
    },

    // ───────────────────────────────
    // Layer 3: Highlight most recent data point
    // Applies conditional KPI color logic (green/yellow/red/black) to the latest value.
    // ───────────────────────────────
    {
      "transform": [
        {
          "filter": {
            "field": "MaxDate",
            "equal": "1" // Filters to the most recent (max) date only.
          }
        }
      ],
      "mark": {
        "type": "circle",  // Circular highlight for the current period.
        "tooltip": true
      },
      "encoding": {
        "color": {
          "condition": [
            {
              "test": "datum[''KPI''] === 2", // KPI = 2 → red (poor)
              "value": "red"
            },
            {
              "test": "datum[''KPI''] === 1", // KPI = 1 → yellow (caution)
              "value": "yellow"
            },
            {
              "test": "datum[''KPI''] === 0", // KPI = 0 → black (neutral)
              "value": "black"
            }
          ],
          "value": "green" // Default color (KPI positive)
        },
        "size": {"value": 50}, // Consistent marker size.
        "x": {
          "field": "Date",
          "type": "temporal"
        },
        "y": {
          "field": "Value",
          "type": "quantitative"
        }
      }
    },

    // ───────────────────────────────
    // Layer 4: Current actual value label
    // Adds a numeric label showing the latest KPI value (formatted number).
    // ───────────────────────────────
    {
      "mark": {
        "type": "text",
        "dx": 0,
        "dy": -10,
        "xOffset": 0,
        "yOffset": -10,             // Offset upward to sit above the point.
        "angle": 0,
        "align": "right",           // Aligns text to the right of last data point.
        "baseline": "bottom",       // Anchors label above the circle marker.
        "font": "sans-serif",
        "fontSize": 20,             // Large size for emphasis.
        "fontStyle": "normal",
        "fontWeight": "normal",
        "limit": 0                  // Ensures full text visibility.
      },
      "encoding": {
        "x": {
          "aggregate": "max",       // Anchored to latest date.
          "field": "Date",
          "type": "temporal"
        },
        "y": {
          "aggregate": "max",       // Anchored to latest value.
          "field": "Value",
          "type": "quantitative"
        },
        "text": {
          "field": "Actual",        // Displays actual KPI value text.
          "format": ".2s"           // Short numeric format (e.g., 1.2k, 3.5M).
        }
      }
    },

    // ───────────────────────────────
    // Layer 5: Variance annotation label
    // Displays variance or supplementary label below the main value.
    // ───────────────────────────────
    {
      "mark": {
        "type": "text",
        "dx": 0,
        "dy": -10,
        "xOffset": 0,
        "yOffset": -10,
        "angle": 0,
        "align": "right",           // Keeps same horizontal alignment.
        "baseline": "top",          // Sits just below main text label.
        "font": "sans-serif",
        "fontSize": 14,             // Slightly smaller for secondary text.
        "fontStyle": "normal",
        "fontWeight": "normal",
        "limit": 0
      },
      "encoding": {
        "x": {
          "aggregate": "max",       // Aligns with most recent date.
          "field": "Date",
          "type": "temporal"
        },
        "y": {
          "aggregate": "max",       // Aligns vertically with most recent value.
          "field": "Value",
          "type": "quantitative"
        },
        "text": {
          "field": "Format Variance" // Displays variance description or formatted delta.
        }
      }
    }
  ]
}'
