Literal.Value:
'{
  "data": {"name": "dataset"}, // dataset binding (Power BI/Deneb data source)

  "layer": [

    // ───────────────────────────────
    // Layer 1: Base Area Fill (context region)
    // ───────────────────────────────
    {
      "mark": {"type": "area"}, // area mark for KPI trend background
      "encoding": {
        "color": {"value": "lightgray"}, // static light-gray fill (background shading)
        "x": {
          "field": "Date",               // temporal field on x-axis (time progression)
          "type": "temporal",
          "axis": null                   // hides axis for clean card layout
        },
        "y": {
          "field": "Value",              // quantitative KPI metric (Y-axis)
          "type": "quantitative",
          "axis": null                   // suppresses axis ticks and labels
        }
      }
    },

    // ───────────────────────────────
    // Layer 2: KPI Trend Line (main visual signal)
    // ───────────────────────────────
    {
      "mark": {
        "type": "line",                  // overlay line trace on area
        "point": {                       // circular points on each data step
          "filled": false,               // outlined circles
          "fill": "white"                // interior color (white fill)
        },
        "tooltip": true                  // tooltips active on hover
      },
      "encoding": {
        "color": {"value": "gray"},      // static gray stroke (neutral trend line)
        "x": {
          "field": "Date",
          "type": "temporal"             // continuous time scale
        },
        "y": {
          "field": "Value",
          "type": "quantitative"         // KPI numeric measure
        }
      }
    },

    // ───────────────────────────────
    // Layer 3: Circle at Latest Date (conditional KPI state marker)
    // ───────────────────────────────
    {
      "transform": [
        {
          "filter": {                    // filters dataset to last record (MaxDate = 1)
            "field": "MaxDate",
            "equal": "1"
          }
        }
      ],
      "mark": {
        "type": "circle",                // mark for latest KPI data point
        "tooltip": true                  // tooltip enabled for inspection
      },
      "encoding": {
        "color": {                       // conditionally colored marker
          "condition": [
            {"test": "datum[''KPI''] === 2", "value": "red"},     // poor performance
            {"test": "datum[''KPI''] === 1", "value": "yellow"},  // neutral/medium
            {"test": "datum[''KPI''] === 0", "value": "black"}    // baseline/neutral
          ],
          "value": "green"               // fallback (good performance)
        },
        "size": {"value": 30},           // circle size constant
        "x": {
          "field": "Date",               // temporal position (latest date)
          "type": "temporal"
        },
        "y": {
          "field": "Value",              // KPI value at latest date
          "type": "quantitative"
        }
      }
    },

    // ───────────────────────────────
    // Layer 4: Actual KPI Label (headline value)
    // ───────────────────────────────
    {
      "mark": {
        "type": "text",                  // textual data label (top value)
        "dx": 0,
        "dy": -10,
        "xOffset": 0,
        "yOffset": -10,
        "angle": 0,
        "align": "right",                // aligns label to right side
        "baseline": "bottom",            // anchored above data point
        "font": "sans-serif",
        "fontSize": 20,                  // main KPI display size
        "fontStyle": "normal",
        "fontWeight": "normal",
        "limit": 0                       // prevents text clipping
      },
      "encoding": {
        "x": {
          "aggregate": "max",            // positions at max (latest) date
          "field": "Date",
          "type": "temporal"
        },
        "y": {
          "aggregate": "max",            // aligns vertically with latest KPI value
          "field": "Value",
          "type": "quantitative"
        },
        "text": {
          "field": "Actual",             // main KPI value to display
          "format": ".2s"                // short-format (1.2k, 3.4M, etc.)
        }
      }
    },

    // ───────────────────────────────
    // Layer 5: Variance Label (secondary metric annotation)
    // ───────────────────────────────
    {
      "mark": {
        "type": "text",                  // text for variance commentary
        "dx": 0,
        "dy": -10,
        "xOffset": 0,
        "yOffset": -10,
        "angle": 0,
        "align": "right",
        "baseline": "top",               // positions slightly below actual label
        "font": "sans-serif",
        "fontSize": 14,                  // smaller for subtext
        "fontStyle": "normal",
        "fontWeight": "normal",
        "limit": 0
      },
      "encoding": {
        "x": {
          "aggregate": "max",            // aligned with same x position (latest date)
          "field": "Date",
          "type": "temporal"
        },
        "y": {
          "aggregate": "max",            // anchored vertically at top KPI point
          "field": "Value",
          "type": "quantitative"
        },
        "text": {
          "field": "Format Variance"     // displays KPI variance descriptor (e.g., “+3% vs Plan”)
        }
      }
    }
  ]
}'
