Literal.Value:
'{
  // Data source connected to the Power BI dataset
  "data": {"name": "dataset"},

  // Use of multiple layered marks for composite KPI visualization
  "layer": [

    // === LAYER 1: Historical trend line ===
    {
      "mark": {
        "type": "line",              // continuous trend line
        "interpolate": "natural",    // smooth curve interpolation
        "tooltip": true              // enable hover tooltip
      },
      "encoding": {
        "color": {"value": "gray"},  // neutral line color
        "x": {
          "field": "Date",
          "type": "temporal",        // date/time field on X-axis
          "axis": null               // hides axis for minimalist design
        },
        "y": {
          "field": "Value",
          "type": "quantitative",    // numeric measure
          "axis": null               // hides Y-axis
        }
      }
    },

    // === LAYER 2: Latest data point (highlight) ===
    {
      "transform": [
        {
          "filter": {
            "field": "MaxDate",      // flag field identifying most recent point
            "equal": "1"
          }
        }
      ],
      "mark": {
        "type": "circle",
        "tooltip": true
      },
      "encoding": {
        "color": {
          // Conditional color logic based on KPI state
          "condition": [
            {"test": "datum[''KPI''] === 2", "value": "red"},    // poor performance
            {"test": "datum[''KPI''] === 1", "value": "yellow"}, // warning
            {"test": "datum[''KPI''] === 0", "value": "black"}   // neutral/default
          ],
          "value": "green"  // default (good performance)
        },
        "size": {"value": 50},  // circle marker size
        "x": {
          "field": "Date",
          "type": "temporal"
        },
        "y": {
          "field": "Value",
          "type": "quantitative"
        }
      }
    },

    // === LAYER 3: Minimum value marker ===
    {
      "mark": {
        "type": "circle",
        "tooltip": true
      },
      "encoding": {
        "color": {"value": "teal"},  // marker color for min point
        "size": {"value": 50},
        "x": {
          "aggregate": {"argmin": "Value"}, // finds date at min value
          "field": "Date",
          "type": "temporal"
        },
        "y": {
          "aggregate": {"argmin": "Value"}, // plots min value
          "field": "Value",
          "type": "quantitative"
        }
      }
    },

    // === LAYER 4: Maximum value marker ===
    {
      "mark": {
        "type": "circle",
        "tooltip": true
      },
      "encoding": {
        "color": {"value": "black"},  // marker color for max point
        "size": {"value": 50},
        "x": {
          "aggregate": {"argmax": "Value"}, // finds date at max value
          "field": "Date",
          "type": "temporal"
        },
        "y": {
          "aggregate": {"argmax": "Value"}, // plots max value
          "field": "Value",
          "type": "quantitative"
        }
      }
    },

    // === LAYER 5: Latest actual value label (formatted number) ===
    {
      "mark": {
        "type": "text",
        "dx": 0,
        "dy": -10,
        "xOffset": 0,
        "yOffset": -10,
        "align": "right",
        "baseline": "bottom",
        "font": "sans-serif",
        "fontSize": 20,
        "fontWeight": "normal"
      },
      "encoding": {
        "x": {
          "aggregate": "max",          // positions at latest (max date)
          "field": "Date",
          "type": "temporal"
        },
        "y": {
          "aggregate": "max",          // aligns vertically with latest value
          "field": "Value",
          "type": "quantitative"
        },
        "text": {
          "field": "Actual",           // field holding actual KPI value
          "format": ".2s"              // short numeric format (e.g., 2.5k)
        }
      }
    },

    // === LAYER 6: Variance label (context annotation) ===
    {
      "mark": {
        "type": "text",
        "dx": 0,
        "dy": -10,
        "xOffset": 0,
        "yOffset": -10,
        "align": "right",
        "baseline": "top",
        "font": "sans-serif",
        "fontSize": 14,
        "fontWeight": "normal"
      },
      "encoding": {
        "x": {
          "aggregate": "max",          // position at same point as previous label
          "field": "Date",
          "type": "temporal"
        },
        "y": {
          "aggregate": "max",
          "field": "Value",
          "type": "quantitative"
        },
        "text": {
          "field": "Format Variance"   // secondary label (e.g., +5%, -3%)
        }
      }
    }
  ]
}'
