Literal.Value:
'{
  "data": {"name": "dataset"}, // data source reference for KPI input fields

  "encoding": {
    "x": {
      "type": "quantitative",         // continuous numeric axis
      "scale": {"nice": false},       // disables auto "nice" rounding of axis domain
      "title": null,                  // removes axis title for compact layout
      "axis": null                    // hides axis ticks and labels entirely
    }
  },

  "layer": [

    // ───────────────────────────────
    // Layer 1: Plan Range Bar (background track)
    // ───────────────────────────────
    {
      "mark": {
        "type": "bar",                // base horizontal reference bar
        "color": "#ccc",              // neutral gray background color
        "size": 40,                   // thicker for visual context
        "yOffset": 10,                // vertical offset to center bar
        "tooltip": true               // enables hover tooltip
      },
      "encoding": {
        "x": {"field": "Plan"},       // total plan value width
        "y": {
          "title": null               // no Y-axis title (minimalist style)
        }
      }
    },

    // ───────────────────────────────
    // Layer 2: Actual Value Bar (foreground performance)
    // ───────────────────────────────
    {
      "mark": {
        "type": "bar",                // overlay smaller bar for actuals
        "size": 20,                   // narrower than base bar
        "yOffset": 10,                // vertically centered
        "tooltip": true               // show exact value on hover
      },
      "encoding": {
        "x": {
          "field": "Value",           // actual metric value
          "title": null
        },
        "color": {                    // KPI conditional color logic
          "condition": [
            { "test": "datum[''KPI''] === 2", "value": "red" },     // critical (below target)
            { "test": "datum[''KPI''] === 1", "value": "yellow" },  // warning
            { "test": "datum[''KPI''] === 0", "value": "black" }    // neutral
          ],
          "value": "green"             // default success color
        }
      }
    },

    // ───────────────────────────────
    // Layer 3: Target Marker (KPI threshold tick)
    // ───────────────────────────────
    {
      "mark": {
        "type": "tick",               // thin vertical line to indicate KPI target
        "color": "orange",            // orange stands out as benchmark indicator
        "size": 50,                   // tick length (horizontal extent)
        "yOffset": 10,                // aligns vertically with bars
        "thickness": 2,               // thickness of tick mark
        "tooltip": true
      },
      "encoding": {
        "x": {
          "field": "KPITarget Value", // position based on target metric
          "title": null
        }
      }
    },

    // ───────────────────────────────
    // Layer 4: Target Value Label (numeric tick annotation)
    // ───────────────────────────────
    {
      "mark": {
        "type": "text",               // textual annotation near tick
        "color": "orange",            // matches target tick color
        "dx": -5,                     // slight left offset
        "dy": 0,
        "xOffset": 0,
        "yOffset": 50,                // places label below the bar region
        "angle": 0,
        "align": "right",             // aligns with tick mark position
        "baseline": "bottom",         // ensures text sits below tick
        "fontSize": 14,               // small, subtle text
        "fontWeight": "normal",
        "limit": 0
      },
      "encoding": {
        "text": {
          "field": "KPITarget Value", // target numeric value
          "format": ".2s"             // formatted (e.g. 1.2k)
        },
        "x": {
          "field": "KPITarget Value",
          "type": "quantitative"
        }
      }
    },

    // ───────────────────────────────
    // Layer 5: Actual KPI Label (headline number above bar)
    // ───────────────────────────────
    {
      "mark": {
        "type": "text",               // displays KPI actual value
        "dx": 0,
        "dy": 0,
        "xOffset": 0,
        "yOffset": -40,               // positioned above bar
        "angle": 0,
        "align": "right",             // right-aligned with end of bar
        "baseline": "bottom",
        "font": "sans-serif",
        "fontSize": 20,               // large main figure
        "fontStyle": "normal",
        "fontWeight": "normal",
        "limit": 0
      },
      "encoding": {
        "x": {
          "aggregate": "max",         // aligns label at rightmost bar end
          "field": "Plan",
          "type": "quantitative"
        },
        "text": {
          "field": "Actual",          // actual KPI numeric value
          "format": ".2s"             // concise numeric format
        }
      }
    },

    // ───────────────────────────────
    // Layer 6: Variance Label (subtext for performance delta)
    // ───────────────────────────────
    {
      "mark": {
        "type": "text",               // descriptive variance label
        "dx": 0,
        "dy": 0,
        "xOffset": 0,
        "yOffset": -40,               // same vertical level as KPI label
        "angle": 0,
        "align": "right",
        "baseline": "top",            // placed slightly below headline number
        "font": "sans-serif",
        "fontSize": 14,               // smaller, secondary text
        "fontStyle": "normal",
        "fontWeight": "normal",
        "limit": 0
      },
      "encoding": {
        "x": {
          "aggregate": "max",         // right-aligns with KPI label
          "field": "Plan",
          "type": "quantitative"
        },
        "text": {
          "field": "Format Variance"  // textual or numeric variance descriptor
        }
      }
    }
  ]
}'
