Literal.Value:
'{
  "data": {"name": "dataset"}, // References a bound dataset containing Date, Value, and KPI-related fields

  "layer": [

    // ───────────────────────────────
    // Layer 1: Primary Area Band (gray shading)
    // Represents the region between Value and KPITarget Value.
    // This layer visualizes performance gaps above or below the target.
    // ───────────────────────────────
    {
      "mark": {"type": "area"},
      "encoding": {
        "color": {"value": "gray"},           // dark gray for band emphasis
        "y": {
          "field": "Value",                   // upper edge of the shaded area
          "type": "quantitative",
          "axis": false                       // hide y-axis to keep layout clean
        },
        "y2": {
          "field": "KPITarget Value",         // lower edge of shaded area
          "type": "quantitative",
          "title": null,
          "axis": false
        },
        "x": {
          "field": "Date",                    // x-axis maps time progression
          "type": "temporal",
          "axis": false                       // suppress axis for compact KPI display
        }
      }
    },

    // ───────────────────────────────
    // Layer 2: Secondary Area Band (light gray overlay)
    // Adds visual contrast by layering a lighter tone behind or over data range.
    // This visually differentiates positive and negative regions when overlapping.
    // ───────────────────────────────
    {
      "mark": {"type": "area"},
      "encoding": {
        "color": {"value": "lightgray"},      // lighter gray fill
        "y": {
          "field": "Value",                   // top of the filled area
          "type": "quantitative"
        },
        "y2": {
          "field": "KPITarget Value",         // bottom (target reference)
          "type": "quantitative"
        },
        "x": {
          "field": "Date",                    // time series reference
          "type": "temporal"
        }
      }
    },

    // ───────────────────────────────
    // Layer 3: KPI Target Line (orange)
    // Draws a continuous reference line through target values.
    // Highlights desired performance trajectory over time.
    // ───────────────────────────────
    {
      "mark": "line",
      "encoding": {
        "color": {"value": "orange"},         // orange = target benchmark
        "x": {
          "field": "Date",
          "type": "temporal",
          "title": "Year"                     // x-axis title (optional display)
        },
        "y": {
          "field": "KPITarget Value",
          "type": "quantitative",
          "title": "Value",                   // y-axis label for context
          "axis": {
            "format": "f",                    // fixed format (no scientific notation)
            "tickCount": 3                    // limits tick density for KPI compactness
          }
        }
      }
    },

    // ───────────────────────────────
    // Layer 4: Actual KPI Line (gray)
    // Tracks actual performance values across time.
    // Sits above the area layers and provides the main comparison signal.
    // ───────────────────────────────
    {
      "mark": "line",
      "encoding": {
        "color": {"value": "gray"},           // neutral line color
        "x": {
          "field": "Date",
          "type": "temporal"
        },
        "y": {
          "field": "Value",
          "type": "quantitative"
        }
      }
    },

    // ───────────────────────────────
    // Layer 5: Actual Value Label (numeric KPI headline)
    // Displays the latest actual KPI figure, positioned at the chart’s endpoint.
    // ───────────────────────────────
    {
      "mark": {
        "type": "text",
        "dx": 0,
        "dy": -10,
        "xOffset": 0,
        "yOffset": -10,                       // vertical offset above the end point
        "angle": 0,
        "align": "right",                     // right-aligned to the end of line
        "baseline": "bottom",
        "font": "sans-serif",
        "fontSize": 20,                       // large numeric emphasis
        "fontStyle": "normal",
        "fontWeight": "normal",
        "limit": 0
      },
      "encoding": {
        "x": {
          "aggregate": "max",                 // anchor to most recent date
          "field": "Date",
          "type": "temporal"
        },
        "y": {
          "aggregate": "max",                 // position at max corresponding Value
          "field": "Value",
          "type": "quantitative"
        },
        "text": {
          "field": "Actual",                  // actual KPI label content
          "format": ".2s"                     // short number format (e.g. 1.5k)
        }
      }
    },

    // ───────────────────────────────
    // Layer 6: Variance Label (subtext for context)
    // Typically used to display variance or trend description.
    // Positioned just below the actual KPI text.
    // ───────────────────────────────
    {
      "mark": {
        "type": "text",
        "dx": 0,
        "dy": -10,
        "xOffset": 0,
        "yOffset": -10,                       // matches KPI label height offset
        "angle": 0,
        "align": "right",
        "baseline": "top",                    // slight vertical separation below the numeric label
        "font": "sans-serif",
        "fontSize": 14,                       // smaller for secondary text
        "fontStyle": "normal",
        "fontWeight": "normal",
        "limit": 0
      },
      "encoding": {
        "x": {
          "aggregate": "max",                 // align at latest point in time
          "field": "Date",
          "type": "temporal"
        },
        "y": {
          "aggregate": "max",
          "field": "Value",
          "type": "quantitative"
        },
        "text": {
          "field": "Format Variance"          // text label explaining variance or KPI status
        }
      }
    }
  ]
}'
