Literal.Value:
'{
  "data": {"name": "dataset"}, // Data source reference (Power BI dataset binding)

  "encoding": {
    "x": {
      "type": "quantitative",           // Numeric scale for KPI metrics (Plan, Value, Target)
      "scale": {"nice": false},         // Prevents Vega from rounding domain for precise KPI alignment
      "title": null,                    // Removes axis title for clean layout
      "axis": null                      // Hides x-axis grid/labels for minimalist card design
    }
  },

  "layer": [

    // --- Layer 1: Background Plan Bar (Gray baseline bar) ---
    {
      "mark": {
        "type": "bar",                  // Base bar showing KPI plan or reference value
        "color": "#ccc",                // Light gray background
        "size": 40,                     // Bar thickness
        "yOffset": 10,                  // Slight vertical offset for alignment
        "tooltip": true                 // Enables tooltip display on hover
      },
      "encoding": {
        "x": {"field": "Plan"},         // Plan value determines bar width
        "y": {"title": null}            // No y-axis label
      }
    },

    // --- Layer 2: Actual Value Bar (Black overlay) ---
    {
      "mark": {
        "type": "bar",                  // Foreground bar showing actual KPI performance
        "color": "black",               // Actual bar color
        "size": 20,                     // Thinner than Plan for contrast
        "yOffset": 10,                  // Aligns visually with Plan bar
        "tooltip": true                 // Shows actual value on hover
      },
      "encoding": {
        "x": {
          "field": "Value",             // Encodes actual KPI value position
          "title": null                 // Hides axis title
        }
      }
    },

    // --- Layer 3: KPI Target Tick Marker (Orange line) ---
    {
      "mark": {
        "type": "tick",                 // Vertical tick marker
        "color": "orange",              // Orange color for emphasis
        "size": 50,                     // Height of the tick line
        "yOffset": 10,                  // Aligns to bar level
        "thickness": 2,                 // Tick line width
        "tooltip": true                 // Tooltip showing target value
      },
      "encoding": {
        "x": {
          "field": "KPITarget Value",   // Target value field
          "title": null                 // No axis label
        }
      }
    },

    // --- Layer 4: Target Value Label (Below tick) ---
    {
      "mark": {
        "type": "text",                 // Text label for target value
        "color": "orange",              // Matches target tick color
        "dx": -5,                       // Small horizontal offset to the left
        "dy": 0,                        // No vertical offset adjustment
        "xOffset": 0,                   // Consistent horizontal offset base
        "yOffset": 50,                  // Pushes text below the tick marker
        "angle": 0,                     // Horizontal text
        "align": "right",               // Aligns with tick marker position
        "baseline": "bottom",           // Anchors label below tick
        "fontSize": 14,                 // Small caption size
        "fontWeight": "normal",         // Regular weight text
        "limit": 0                      // No truncation on long text
      },
      "encoding": {
        "text": {
          "field": "KPITarget Value",   // Value displayed as text
          "format": ".2s"               // Short number format (e.g., 1.2k)
        },
        "x": {
          "field": "KPITarget Value",   // Positions label horizontally at target
          "type": "quantitative"        // Quantitative field type for scaling
        }
      }
    },

    // --- Layer 5: Actual KPI Value Label (Main figure) ---
    {
      "mark": {
        "type": "text",                 // Text mark showing actual KPI value
        "dx": 0,                        // No horizontal nudge
        "dy": 0,                        // No vertical nudge
        "xOffset": 0,                   // Keep alignment consistent
        "yOffset": -40,                 // Position label above bars
        "angle": 0,                     // Standard horizontal text
        "align": "right",               // Right-aligned (flush with bar edge)
        "baseline": "bottom",           // Sits above the bar
        "font": "sans-serif",           // Font family for consistency
        "fontSize": 20,                 // Large KPI display font
        "fontStyle": "normal",
        "fontWeight": "normal",
        "limit": 0                      // Do not truncate text
      },
      "encoding": {
        "x": {
          "aggregate": "max",           // Aligns label with rightmost edge of bar
          "field": "Plan",              // Uses plan as positional anchor
          "type": "quantitative"
        },
        "text": {
          "field": "Actual",            // Displays actual KPI value
          "format": ".2s"               // Short numeric format (e.g., 1.2k, 3.4M)
        }
      }
    },

    // --- Layer 6: Variance Label (Supplementary description) ---
    {
      "mark": {
        "type": "text",                 // Text mark showing variance from target
        "dx": 0,
        "dy": 0,
        "xOffset": 0,
        "yOffset": -40,                 // Positioned near the main KPI label
        "angle": 0,
        "align": "right",               // Aligns with KPI label
        "baseline": "top",              // Slightly below the actual KPI label
        "font": "sans-serif",
        "fontSize": 14,                 // Smaller for secondary data
        "fontStyle": "normal",
        "fontWeight": "normal",
        "limit": 0
      },
      "encoding": {
        "x": {
          "aggregate": "max",           // Aligns with main KPI anchor
          "field": "Plan",              // Shared base field
          "type": "quantitative"
        },
        "text": {
          "field": "Format Variance"    // Displays variance text (e.g., "+5% vs Plan")
        }
      }
    }
  ]
}'
